	}

	.login-page .new-session-tabs li:not(.active) {
		background-color: var(--main-background);
	}

	.login-page .new-session-tabs {
		box-shadow: 0 0 0 1px var(--main-border);
	}

	.login-page .new-session-tabs li {
		border-color: var(--main-border);
	}

	.nav-tabs .nav-link.active, .nav-tabs .nav-item.show .nav-link {
		background-color: #3b3f4e;
	}

	.nav-links:not(.quick-links) li:not(.md-header-toolbar) a, .nav-links:not(.quick-links) li:not(.md-header-toolbar) button {
		color: var(--main-text-color);
	}

	.nav-links:not(.quick-links) li:not(.md-header-toolbar).active a, .nav-links:not(.quick-links) li:not(.md-header-toolbar).active button, .nav-links:not(.quick-links) li:not(.md-header-toolbar) a.active {
		color: var(--lighter-text-color-3);
	}

	.nav-links:not(.quick-links) li:not(.md-header-toolbar) a:hover, .nav-links:not(.quick-links) li:not(.md-header-toolbar) .participants-more .btn-link:hover, .participants-more .nav-links:not(.quick-links) li:not(.md-header-toolbar) .btn-link:hover, .nav-links:not(.quick-links) li:not(.md-header-toolbar) .user-list-more .btn-link:hover, .user-list-more .nav-links:not(.quick-links) li:not(.md-header-toolbar) .btn-link:hover, .nav-links:not(.quick-links) li:not(.md-header-toolbar) a:active, .nav-links:not(.quick-links) li:not(.md-header-toolbar) a:focus, .nav-links:not(.quick-links) li:not(.md-header-toolbar) button:hover, .nav-links:not(.quick-links) li:not(.md-header-toolbar) button:active, .nav-links:not(.quick-links) li:not(.md-header-toolbar) button:focus {
		border-color: #2a66aa;
		color: var(--lighter-text-color-3);
	}

	.login-page .form-control:active, .login-page .search form:active, .search .login-page form:active, .login-page .form-control:focus, .login-page .common-note-form .md-area.is-focused, .common-note-form .login-page .md-area.is-focused, .login-page .search form:focus, .search .login-page form:focus, .login-page .search.search-active form, .search.search-active .login-page form {
		background-color: #313541;
	}

	.form-control:focus, .common-note-form .md-area.is-focused, .search form:focus, .search.search-active form {
		color: var(--main-text-color);
	}

	.gl-show-field-errors .gl-field-hint {
		color: #d3d3d3;;
	}

	/* ===== Authorize external website/application ===== */

	.modal-content {
		background-color: var(--main-background);
	}

	.modal-header {
		background-color: var(--main-background);
		border-color: var(--light-divider);
	}

	.modal-body {
		background-color: var(--main-background);
	}

	.form-actions {
		background-color: var(--main-background);
		border-color: var(--light-divider);
	}

	.modal-doorkeepr-auth .scope-description, .doorkeeper-app-form .scope-description {
		color: #d3d3d3;
	}

	/* ===== Wiki ===== */

	/* Sidebar */

	.right-sidebar.wiki-sidebar a {
		color: var(--darker-text-color-4);
	}

	.right-sidebar.wiki-sidebar .active > a {
		color: var(--lighter-text-color-3);
	}

	/* Page header */

	.wiki-page-header .wiki-last-edit-by {
		color: var(--darker-text-color-4);
	}

	.wiki-page-header .wiki-last-edit-by strong {
		color: var(--main-text-color);
	}

	/* List of pages */

	ul.wiki-pages-list.content-list a {
		color: var(--a-color);
	}

	/* ===== Help ===== */

	.wiki:not(.use-csslab) h1,
	.wiki:not(.use-csslab) h2,
	.wiki:not(.use-csslab) h3 {
		border-color: var(--main-border);
		color: #f5f5f5;
	}

	.wiki:not(.use-csslab) {
		color: var(--main-text-color);
	}

	.wiki:not(.use-csslab) a {
		color: var(--a-color);
	}

	.wiki:not(.use-csslab) img:not(.emoji) {
		border-color: #363a48;
	}

	/* ===== about.gitlab.com ===== */

	/* Features */

	.steps {
		background-color: var(--main-background);
	}

	.steps a {
		color: var(--a-color);
	}

	.steps a:hover {
		border-bottom-color: var(--a-color);
	}

	.steps .step {
		border-color: var(--main-border);
	}

	/* ===== docs.gitlab.com ===== */

	/* Header/Navigation bar */

	.header {
		background-color: var(--lighter-background-1);
		border-color: var(--btn-border);
	}

	.header p {
		color: #eee;
	}

	.header .nav > li > a {
		color: #eee;
	}

	.header input {
		background-color: var(--lighter-background-1);
	}

	.header .btn-versions {
		color: var(--main-text-color);
	}

	.dropdown-menu .divider {
		background-color: var(--light-divider);
	}

	/* Left sidebar */

	.nav-wrapper {
		background-color: var(--lighter-background-1);
		border-color: var(--main-border);
	}

	.nav-wrapper .nav-toggle {
		border-color: var(--lighter-background-1);
		border-top-color: var(--main-border);
	}

	.nav-wrapper .nav-toggle:hover {
		background-color: #383b4a;
	}

	.nav-wrapper .nav-toggle .arrow::before, .nav-wrapper .nav-toggle .arrow::after {
		background-color: var(--main-text-color);
	}

	.nav-wrapper.active .nav-toggle .label {
		color: var(--main-text-color);
	}

	.global-nav a.active {
		background-color: #383b4a;
		border-color: var(--arc-blue);
		color: var(--main-text-color);
	}

	.level-0, .level-1, .level-2 {
		color: var(--darker-text-color-1);
	}

	a.global-nav-link:hover, .nav-link:hover, a.global-nav-link:hover:visited, .nav-link:hover:visited {
		color: var(--lighter-text-color-3);
	}

	a.global-nav-link:visited, .nav-link:visited {
		color: var(--darker-text-color-1);
	}

	.global-nav .collapse-toggle::after {
		border-color: var(--main-text-color);
	}

	.global-nav .collapse-toggle.active.section-title {
		background-color: #383b4a;
	}

	.global-nav .global-nav-section.expanded {
		background-color: var(--lighter-background-1);
	}

	.global-nav a.active.level-1, .global-nav a.active.level-2 {
		background-color: #383b4a;
	}

	.global-nav .collapse-toggle.active {
		background-color: #383b4a;
	}

	.global-nav-badges {
		color: var(--darker-text-color-3);
	}

	/* TOC */

	.doc-nav .toc-collapse {
		color: var(--darker-text-color-5);
	}

	.doc-nav .toc-collapse:hover {
		color: var(--darker-text-color-1);
	}

	.doc-nav .toc-collapse.collapsed {
		background-color: #393e4c;
	}

	.doc-nav>ul li>a {
		color: var(--darker-text-color-3);
	}

	.doc-nav>ul li>a:hover {
		color: var(--lighter-text-color-2);
	}

	.doc-nav > ul li .nav-link.active {
		color: var(--lighter-text-color-2);
	}

	.doc-nav > ul li .nav-link.active:hover, .doc-nav > ul li .nav-link.active:only-child {
		color: var(--lighter-text-color-3);
	}

	/* Main content */

	.main {
		background-color: var(--main-background);
	}

	.doc-nav > ul {
		background-color: var(--lighter-background-1);
	}

	h1 {
		border-color: #5c647b;
	}

	h2, h3, h4, h5, h6 {
		border-color: #545c71;
	}

	table td, table th {
		border-color: #2f333f;
	}

	table thead {
		background-color: var(--main-background);
	}

	table tbody {
		border-color: #2f333f;
	}

	blockquote {
		background-color: #3c4253;
		color: #a7b7c9;
	}

	.alert-info {
		background-color: #163558;
		border-color: #245890;
		color: var(--main-text-color);
	}

	.alert-warning {
		background-color: #7e4100;
		border-color: #a35a00;
		color: var(--main-text-color);
	}

	.alert-success {
		background-color: #083a1b;
		border-color: #0e6c31;
		color: var(--main-text-color);
	}

	.main .edit-on {
		border-color: var(--light-divider);
	}

	h2 code, h3 code, h4 code, h5 code, h6 code {
		background-color: #24272a;
		color: #e2e2e2;
	}

	p code, li code, table code {
		background-color: #24272a;
		color: #e2e2e2;
	}

	a:hover, .participants-more .btn-link:hover, .user-list-more .btn-link:hover, a:focus {
		color: var(--a-hover-color);
	}

	.introduced-in {
		color: var(--darker-text-color-5);
	}

	h2:hover .anchor, h3:hover .anchor, h4:hover .anchor, h5:hover .anchor, h6:hover .anchor {
		filter: invert(75%);
	}

	/* Help and feedback (found towards the bottom of each page) */

	.comments {
		border-color: #4b5265;
		background-color: var(--main-background);
	}

	.tab-content .row {
		border-color: var(--light-divider);
	}

	.comments-filter a.comments-holder {
		background-color: var(--btn-background);
	}

	.comments-filter a.comments-holder:hover {
		background-color: var(--btn-background);
	}

	.comments-filter a {
		border-color: #974642;
		color: var(--main-text-color);
	}

	.comments-filter a:hover {
		color: #e24329;
	}

	.comments-holder:hover {
		box-shadow: none;
	}

	.main .nav-tabs > li > a.active {
		background-color: #353948;
		border-color: var(--main-border);
	}

	.nav-tabs {
		border-color: var(--btn-border);
	}

	.nav-tabs > li > a {
		background-color: var(--main-background);
		border-color: var(--main-border);
		color: var(--main-text);
	}

	.nav-tabs > li > a:hover {
		background-color: #3b3f4e;
		border-color: var(--main-border);
		color: var(--main-text);
	}

