/* ==UserStyle==
@name         Dark-GitLab
@namespace    gitlab.com/vednoc/dark-gitlab
@version      0.56.1
@description  Dark and light theme for GitLab and self-hosted GitLab instances.
@author       vednoc <vednoc@pm.me> (https://gitlab.com/vednoc)
@homepageURL  https://gitlab.com/vednoc/dark-gitlab
@supportURL   https://gitlab.com/vednoc/dark-gitlab/issues
@updateURL    https://gitlab.com/vednoc/dark-gitlab/raw/master/gitlab.user.styl
@preprocessor stylus

@var select   theme      "Theme colors" ["Default", "GitHub"]
@var text     b_radius   "Custom border radius" 4px
@var text     a_radius   "Custom avatar radius" 8px
@var text     max_width  "Custom width for fixed layout" 1280px
@var text     ui_font    "Custom UI font" "'font_name'"
@var text     mono_font  "Custom mono font" "'font_name'"
@var text     emoji_font "Custom emoji font" "'font_name'"
@var text     transition "Custom transitions" 0.15s ease
@var text     faded_img  "Fade images in md/wiki/blog" "1"
@var checkbox gl_icons   "GitLab Icons [Add-on]: Fix icon alignment" 0
@var checkbox p_header   "Center project header elements" 0
@var checkbox md_limit   "Readme: Limit content max-width" 1
@var checkbox md_center  "Readme: Center the content" 1
@var checkbox dark_logo  "Switch to our darker logo" 0
@var checkbox navbar_s   "Navbar: Match custom layout width" 0
@var checkbox navbar_c   "Navbar: Theme navbar color" 1
@var checkbox navbar_n   "Navbar: Show 'next' badge" 1
@var checkbox invert_ic  "Icons: Invert in Chromium-based browsers" 1
@var checkbox enable_sb  "Enable custom scrollbars" 1
@var checkbox bg_invert  "Background invert colors" 0
@var text     bg_image   "Background URL/base64" "#00000025"
@var text     bg_filter  "Background filter" opacity(1) invert(var(--bg-invert))
@var text     bg_size    "Background size" cover
@var text     bg_repeat  "Background repeat" repeat
@var text     bg_pos     "Background position" center

@var color dark    "Color: Primary bg   " #292929
@var color darken  "Color: Highlight bg " #333333
@var color darker  "Color: Secondary bg " #424242
@var color light   "Color: Primary fg   " #e6e6e6
@var color lighter "Color: Secondary fg " #bababa
@var color accent  "Color: Accent       " #cb253b
@var color yellow  "Color: Yellow       " #f0c674
@var color orange  "Color: Orange       " #d98245
@var color red     "Color: Red          " #a54242
@var color magenta "Color: Magenta      " #b294bb
@var color violet  "Color: Violet       " #85678f
@var color blue    "Color: Blue         " #54738c
@var color cyan    "Color: Cyan         " #5e8d87
@var color green   "Color: Green        " #7b823a
@var color white   "Color: White        " #ffffff
@var color shadow  "Color: Shadow       " #00000025
==/UserStyle== */

@-moz-document regexp('^https?://(www\.)?([A-Za-z0-9]+\.)?(gitlab\.(?!(io|biterg.io|exclude.domain)).*|include.domain)(/.*)?$'),
domain('invent.kde.org'),
domain('git.pleroma.social'),
domain('code.videolan.org'),
domain('gitgud.io'),
domain('salsa.debian.org'),
domain('framagit.org'),
domain('source.joinmastodon.org'),
domain('repo.getmonero.org'),
domain('git.gnu.io'),
domain('git.synz.io'),
domain('git.drupalcode.org'),
domain('gitlab.dev.owl.co') {
  :root {
    --note      : 'Dark-GitLab v0.56.1';
    --bshadow   : 0 2px 4px var(--shadow);
    --t         : transparent !important;
    --white     : white;
    --width     : max_width;
    --radius    : b_radius;
    --avatar    : a_radius;
    --ts        : transition;
    --ui-font   : ui_font, -apple-system, BlinkMacSystemFont, 'Segoe UI',
      Roboto, 'Noto Sans', Ubuntu, Cantarell, 'Helvetica Neue',
      var(--emoji-font), sans-serif;
    --mono-font : mono_font, 'Menlo', 'DejaVu Sans Mono', 'Liberation Mono',
      'Consolas', 'Ubuntu Mono', 'Courier New', 'andale mono',
      'lucida console', monospace;
    --emoji-font: emoji_font, 'Apple Color Emoji', 'Segoe UI Emoji',
      'Segoe UI Symbol', 'Noto Color Emoji';
    --bg-invert : bg_invert;
    --bg-filter : bg_filter;
    --bg-size   : bg_size;
    --bg-repeat : bg_repeat;
    --bg-pos    : bg_pos;
    --background: bg_image;

    if (theme == 'GitHub') {
      --background: #282828;
      --dark      : #181818;
      --darken    : #202020;
      --darker    : #404040;
      --light     : #e6e6e6;
      --lighter   : #bfbfbf;
      --accent    : #4f8cc9;
      --shadow    : #00000040;
      --yellow    : #ffcc33;
      --orange    : #fe9600;
      --red       : #ff4444;
      --magenta   : #8368aa;
      --violet    : #6e5494;
      --blue      : #4f8cc9;
      --cyan      : #4d5eff;
      --green     : #008833;
    } else {
      --dark      : dark;
      --darken    : darken;
      --darker    : darker;
      --light     : light;
      --lighter   : lighter;
      --accent    : accent;
      --shadow    : shadow;
      --yellow    : yellow;
      --orange    : orange;
      --red       : red;
      --magenta   : magenta;
      --violet    : violet;
      --blue      : blue;
      --cyan      : cyan;
      --green     : green;
    }
  }

  // Overwrite existing Stylus-lang vars with CSS vars.
  bg      = var(--dark);
  hl      = var(--darken);
  bd      = var(--darker);
  fg      = var(--light);
  cm      = var(--lighter);
  ac      = var(--accent);
  sh      = var(--shadow);
  bsh     = var(--bshadow);
  yellow  = var(--yellow);
  orange  = var(--orange);
  red     = var(--red);
  magenta = var(--magenta);
  violet  = var(--violet);
  blue    = var(--blue);
  cyan    = var(--cyan);
  green   = var(--green);
  white   = var(--white);
  r       = var(--radius);
  t       = var(--t);
  ts      = var(--ts);
  outer   = 0 0 0 1px bd;
  inner   = inset outer;
  i       = !important;

  // Color mixin.
  /// All colors: `c: fg bd bg;`
  /// Only border-color: `c: 0 bd;`
  c(x, y = 0, z = 0, xi = 1, yi = 1, zi = 1) {
    i = !important;
    if x is a 'call' or x is a 'rgba' {            color: xi is 0? x : x i }
    if y is a 'call' or y is a 'rgba' {     border-color: yi is 0? y : y i }
    if z is a 'call' or z is a 'rgba' { background-color: zi is 0? z : z i }
  }
  // SVG colors.
  v(x, y = 0, xi = 1, yi = 1) {
    i = !important;
    if x is a 'call' or x is a 'rgba' {   fill: xi is 0? x : x i }
    if y is a 'call' or y is a 'rgba' { stroke: yi is 0? y : y i }
  }
  // Border radius mixin.
  rad() { border-radius: arguments }

  // * Scrollbars.
  if ( enable_sb == 1 ) {
    html { scrollbar-color: bd bg }
    ::-webkit-scrollbar {
      width: 12px;
      &-track { background: bg }
      &-thumb { background: bd }
    }
  }
  // Input placeholder.
  ::placeholder { c: cm }
  ::-webkit-input-placeholder { c: cm }

  body {
    c: fg 0 t;
    font-family: var(--ui-font);
    // Fixes content shift when modal is open.
    &.modal-open { padding-right: 0px i }
    // Empty navbar.
    &.navless {
      c: fg 0 t;
      height: calc(100% - 41px);
      .navbar-empty { c: 0 bd bg }
    }
    /// Shop navbar.
    header.site-header { c: 0 bd bg }
    // Footer.
    .footer-fixed {
      c: 0 bd bg;
      + .footer-container {
        max-width: 100%;
        c: 0 0 bg;
      }
    }
    // ? Browser defaults.
    time { color: inherit }
    h1, h2, h3, h4, h5, h6, p { c: fg }
    ul, ol { color: fg }
    a {
      transition: ts;
      color: ac;
      &:hover, &:focus { color: ac }
    }
    hr {
      margin: 16px auto;
      c: 0 bd t;
    }
    img { &.transparent, &.lazy { c: 0 bd t }}
    kbd {
      padding: 0.2rem 0.4rem;
      c: fg 0 hl 0 0 0;
      box-shadow: inner, inset 0 -3px bd;
    }
    code {
      c: fg bd hl 0 0 0;
      /// SSH keys.
      &.key-fingerprint { c: cm bd bd }
    }
    pre {
      rad: r;
      c: fg bd hl 0 0 0;
      &.bg-light { c: 0 0 hl }
    }
    pre, code, kbd, samp { font-family: var(--mono-font) }
    /// Reset background for raw files.
    > pre:not([class]) { c: 0 0 t }

    // Checkbox/radio inputs.
    input[type = 'checkbox'], input[type = 'radio'] {
      &, &:focus, &:hover:active, &:disabled, &:hover:active:disabled {
        -webkit-appearance: none i;
        -moz-appearance: none i;
        appearance: none i;
        height: 1em i;
        width: 1em i;
        border: 1px solid i;
        c: fg bd bd;
      }
      &:hover { box-shadow: 0 0 2px 1px cm i }
    }
    input[type = 'radio'] {
      border-radius: 100% i;
      &:checked { c: 0 sh ac }
    }
    input[type = 'checkbox'] {
      &:checked { c: 0 sh ac }
      &:indeterminate { c: 0 sh red }
    }
    .gl-link { color: ac }
  }

  // Global -> Custom checkbox/radio inputs.
  @css {
    input[type = 'radio']:checked {
      background: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 512 512"><circle cx="256" cy="256" r="128" fill="white"/></svg>') !important;
      background-size: cover !important;
    }
    input[type = 'checkbox']:checked {
      background: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 512 512"><path fill="white" d="M99 213l-1 89 104 102 205-201v-91L203 315 99 213z"/></svg>') !important;
      background-size: cover !important;
    }
    input[type = 'checkbox']:indeterminate {
      background: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 512 512"><rect x="128" y="192" width="256" height="96" fill="white"/></svg>') !important;
      background-size: cover !important;
    }
  }

  // * Table -> Base styles for all `table` elements.
  table:not(.code):not(.shortcut-mappings):not(.pika-table) {
    display: table i;
    border-collapse: unset;
    border-spacing: 0px;
    rad: r;
    c: 0 0 bg;
    thead {
      c: 0 0 t;
      &, tr:first-child {
        > th {
          c: fg bd hl;
          border-top: 1px solid bd i;
          border-bottom: 1px solid bd i;
          &:not(:last-child) { border-right: none i }
          &:first-child {
            border-top-left-radius: r;
            border-left: 1px solid bd;
          }
          &:last-child {
            border-top-right-radius: r;
            border-right: 1px solid bd;
          }
        }
      }
    }
    tbody {
      c: 0 0 t;
      td {
        word-break: break-all;
        border: none i;
        border-left: 1px solid bd i;
        border-bottom: 1px solid bd i;
        c: fg bd t;
        &:last-child { border-right: 1px solid bd i }
      }
      tr {
        // AsciiDoc tables and CI/CD jobs.
        > th {
          border: none i;
          border-left: 1px solid bd i;
          border-bottom: 1px solid bd i;
          c: fg 0 hl;
          &[colspan = '10'] { border-right: 1px solid bd i }
        }
        // &:hover { th, td { c: 0 0 hl }}
        &:last-child {
          // Bottom corners look a bit off on Firefox.
          th, td { &:first-child { border-bottom-left-radius: r }}
          td:last-child { border-bottom-right-radius: r }
        }
      }
    }
    &.table-striped tbody {
      tr:nth-of-type(odd) { c: 0 0 t }
      tr:nth-of-type(even) > td { c: 0 0 hl }
    }
    /// #88 @upstream
    /// Who cares about semantic HTML anyways?
    td.d-sm-block { display: table-cell i }
  }
  // Table. #41
  table[frame = 'void'] {
    c: cm;
    box-shadow: none;
    > tbody {
      c: 0 0 bg;
      tr th {
        border-bottom-width: 1px i;
        c: fg bd bd;
      }
    }
  }
  // * Table -> Project settings page or new MR.
  .border-bottom {
    padding-bottom: 10px;
    c: 0 bd;
    > .table {
      thead th {
        padding: 8px i;
        c: fg bd hl;
      }
      tbody td {
        padding: 8px i;
        c: fg bd bg;
      }
    }
  }

  // * Emojis -> Fix emojis being cropped.
  gl-emoji {
    font-size: 1.25em;
    font-family: var(--emoji-font) i;
    // ? An attempt to fix emoji positioning on *fox-based browsers.
    @supports (-moz-user-select: none) {
      vertical-align: text-top i;
    }
  }
  .user-status-emoji { display: inline-flex }
  // ? Remove underline from `label-link` selectors.
  .label-link { text-decoration: none i }

  // Icons.
  svg {
    v: cm;
    /// Diff icons.
    &.ic-file-addition { &, &-solid { v: green }}
    &.ic-file-modified { &, &-solid { v: blue }}
    &.ic-file-deletion { &, &-solid { v: red }}
  }
  .fa {
    c: cm;
    &[class *= 'online'] { c: green }
    /// Assignee can't merge.
    &-exclamation-triangle { c: orange }
  }
  // * Icons -> Global icons that don't work without `path`.
  // ? Calendar, add reaction, commit icon.
  .nav-controls .has-tooltip svg, .award-control-icon svg, .commit-icon {
    path { v: cm }
  }
  // Sidebar -> Operations icon. @upstream
  #cloud-gear path { v: cm }

  // * Global -> Bootstrap reset.
  .bg {
    &-white { c: 0 0 bg }
    &-info { c: 0 0 bd }
    &-light { c: 0 0 bg }
    &-secondary { c: 0 0 bg }
    &-gray-light { c: 0 0 bg }
  }
  .text {
    &-info { c: blue }
    &-white { c: fg }
    &-danger { c: red }
    &-warning { c: orange }
    &-success { c: green }
    &-tertiary { c: cm }
    &-secondary { c: fg }
  }
  .badge {
    &.unverified { c: cm bd bg }
    &.verified { c: green green bg }
    &-success { c: white 0 green }
    &-primary { c: white 0 blue }
    &-info { c: white 0 blue }
    &-gray { c: fg bd bd }
    &-danger { c: white 0 red }
    &-warning { c: black 0 orange }
    // Resets.
    &&-pill {
      c: cm bd hl;
      box-shadow: inner;
      // Dashboard -> Activities.
      &.issues-count { c: white 0 green }
      &.merge-requests-count { c: white 0 orange }
      &.todos-count { c: white 0 blue }
    }
    /// HACK: Reset colored labels.
    &.color-label {
      text-decoration: none i;
      box-shadow: inset 0 0 0 1rem #33333370;
      &:hover { box-shadow: none i }
      &[style *= 'color: #333333'] {
        c: black;
        box-shadow: inset 0 0 0 1rem #33333325;
      }
    }
  }
  // Text colors.
  .danger-title { c: red }
  .warning-title { c: orange }
  .cgray, .committed_ago, .hint, .help-block { c: cm }
  .light, .description-block, .task-list-item { c: fg }
  .account-btn-link, [class *= 'settings-sidebar'] a { color: ac }
  /// #98
  .commit a, /.generic-commit-status a { color: fg }

  // * Global -> Alerts.
  .bs-callout {
    rad: 4px;
    position: relative;
    c: fg bd hl;
    box-shadow: inner;
    &.gcp-signup-offer { border-left-color: blue i }
    &.help-callout, &.shared-runners-description {
      border-left: none;
      box-shadow: inner;
      &::before {
        content: '';
        position: absolute;
        top: 0; left: 0; bottom: 0;
        rad: 4px 0 0 4px;
        border-left: 4px solid bd;
      }
    }
    &&-info {
      border: none;
      box-shadow: inset 0 0 0 1px blue;
      &::before {
        content: '';
        position: absolute;
        top: 0; left: 0; bottom: 0;
        rad: 4px 0 0 4px;
        border-left: 4px solid blue;
      }
    }
    &&-warning {
      border: none;
      box-shadow: inset 0 0 0 1px orange;
      a { c: ac }
      &::before {
        content: '';
        position: absolute;
        top: 0; left: 0; bottom: 0;
        rad: 4px 0 0 4px;
        border-left: 4px solid orange;
      }
    }
    /// 11.3 -> Privacy policy update.
    /[data-page *= 'projects'] &.js-privacy-policy-update {
      margin: 0 auto;
      max-width: calc(var(--width) - 32px);
    }
  }

  // * Global -> Scoped labels.
  .scoped-label {
    .fa-question-circle {
      c: 0 0 t;
      &[style *= 'color: #FFFFFF']  { c: white }
      &[style *= 'color: #333333']  { c: black }
    }
  }

  // * Button reset.
  .btn {
    // ? Base selectors.
    &, .btn-default {
      transition: ts;
      c: cm bd bg 0 0 0;
      // &:hover, &:focus, &.focus {
      //   c: fg bd hl 0 0 0;
      //   box-shadow: none i;
      // }
    }
    &:not(:disabled):not(.disabled) {
      &:hover, &:focus, &.focus, &:active {
        c: fg bd hl 0 0 0;
        box-shadow: none;
      }
    }
    &.active, &.is-active {
      c: fg bd bd;
      box-shadow: none i;
      svg { v: fg }
    }
    &-secondary {
      &:not(:disabled):not(.disabled) {
        &:active, &.active { c: fg bd bd }
      }
    }
    &-link {
      c: cm t t;
      &:hover { c: 0 0 t }
    }
    // Green buttons.
    &-success, &-register, &-new, &-create, &-save,
    &.comment-btn, .comment-btn, .commit-btn {
      c: white green green;
      &:hover { opacity: 0.75 }
      .fa { color: inherit i }
    }
    &-success {
      &.btn-inverted { c: green green bg }
      svg { fill: currentColor i }
    }
    // Blue buttons.
    &-info, &-primary, &-register {
      c: white blue blue;
      &:hover { opacity: 0.75 }
    }
    &-primary&-inverted { c: ac ac bg }
    // Orange buttons.
    &-warning {
      c: white orange orange;
      &:hover { opacity: 0.75 }
    }
    // Close buttons.
    &-close, &-close-color {
      c: red red bg;
      &:hover { c: white 0 red }
    }
    &-close-color.dropdown-toggle {
      c: red red bg;
      .fa { color: inherit i }
      &:hover { c: white 0 red }
    }
    // Remove buttons.
    &-danger, &-remove, &-red, &-inverted.btn-remove {
      c: white red red;
      .fa { c: white }
      &:hover { opacity: 0.75 }
    }
    // Blank buttons.
    &-cancel, &-retry {
      c: cm bd bg;
      &:hover, &:focus { c: fg 0 hl }
    }
    &-blank { c: cm 0 t }
    &-clipboard {
      c: cm bd t;
      &:hover { c: fg }
      &:hover svg { v: fg }
    }
    // Comment on a note.
    &-text-field { c: cm bd hl }
    // Colors for buttons with dashed borders.
    &-missing {
      c: cm bd bg;
      &:hover { c: fg 0 hl }
    }
    // Disabled buttons.
    &.disabled, &[disabled] {
      &, &:hover {
        opacity: 0.6 i;
        c: cm bd bg;
      }
      i[class] { c: cm }
    }
    // Project -> Download source code.
    &&-xs, &-xs.stat-text {
      padding: 8px 10px;
      &.btn-primary { c: fg 0 hl }
    }
    /// Operations -> New flag.
    &.clear-search-input { c: 0 0 t }
    /// Notes -> Reactions.
    &.award-control {
      padding: 5px 10px;
      &.user-authored { c: fg bd bg }
      &.active { c: fg blue bd }
    }
  }
  // Button groups.
  .input-group {
    &-btn, &-prepend, &-append {
      c: 0 0 t;
      .btn:focus { c: 0 0 bd }
    }
  }

  // Global -> Toggles.
  .project-feature-toggle, .gl-toggle {
    c: 0 0 bd;
    &.is-checked { c: 0 0 ac }
    .toggle-icon { c: 0 0 bg }
  }

  // Issue/Merge status.
  .status-box {
    &-mr-merged { c: white 0 blue }

    &-mr-closed { c: white 0 red }
    &-open { c: white 0 green }
    &-issue-closed { c: white 0 blue }
    &-expired { c: white 0 orange }
    &-closed { c: white 0 red }
  }
  // Confidential icon.
  .issuable-warning-icon {
    c: 0 0 bd;
    svg.icon { v: orange }
  }

  // * Global -> Random styles.
  a.tag-release-link,
  [class *= 'committer-link'],
  [class *= 'author-link'],
  .ci-table .commit-sha,
  .ci-table .ref-name,
  .js-user-link,
  .author_name a {
    c: ac;
  }
  .ci-table .build-link,
  .ci-table .pipeline-id,
  .label-bold { color: fg }
  .cred { c: red; v: red }
  .cblue { c: blue; v: blue }
  .cgreen { c: green; v: green }
  .diff-file-changed-icon { &:not(.cgreen):not(.cred) { v: blue }}
  // ? Move to a better place.
  .option {
    &-title { c: fg }
    &-description, &-disabled-reason { c: cm }
  }
  .bordered-box {
    rad: r;
    c: 0 bd;
  }
  // ? Check #13 for more info.
  .deployment-info, .js-deploy-time { c: fg }
  // * Grid styles.
  .flex-grid {
    .grid-row { c: 0 bd }
    .grid-cell {
      c: 0 bd;
      .property-label { c: cm }
    }
  }
  // ? Promotion background on i.e. project/audit_events page.
  .promotion-backdrop { c: 0 0 t }
  // ? Custom GitLab tables on i.e. project/settings/badges page.
  .gl-responsive-table-row-layout {
    c: 0 bd;
    // Items border.
    .table-section:not(:first-child) { c: 0 bd }
    // Text badges.
    .badge.badge-pill { c: fg 0 bd }
    // Footer.
    .table-button-footer {
      c: 0 bd t;
      @media screen and (max-width: 767px) { c: 0 0 bd }
    }
  }
  // ? Pipelines: Fix table corners and item borders.
  .gl-responsive-table-row {
    color: cm;
    &:not(:last-child) { border-bottom: 1px solid bd }
    &:first-child { rad: r r 0 0 }
    &:last-child { rad: 0 0 r r }
  }
  // ? 404 page.
  img[alt = '404'] + .container {
    margin-bottom: 1em;
    c: 0 bd;
  }
  // ? Forms.
  .form-control {
    c: cm bd bg;
    input.add-issuable-form-input { c: 0 0 t }
    &:focus, &.focus {
      c: fg blue;
      box-shadow: none;
      &:hover { box-shadow: none }
    }
    &:disabled, &[readonly] { c: cm bd }
    /// #80.
    &[id] + button { c: 0 bd }
    /// Rewrite inputs now that they are native components.
    /.gl-form-input {
      font-family: var(--ui-font) i;
      box-shadow: inner i;
    }
  }
  .label-form .form-group {
    // Project -> New labels.
    code {
