    > .top-area:not(.cover-block) {
      margin-top: 8px;
      rad: r;
      c: 0 0 bg;
      box-shadow: inner;
      > .nav.nav-tabs:not(#z) {
        a { padding: 10px 8px }
        margin-top: 0;
        c: 0 t t;
        box-shadow: none;
      }
      // ../project/pipeline_schedules
      + .card { margin-top: 8px }
      // Project -> Tag list/details and wiki.
      &.adjust, &.multi-line, &.wiki-page-header { padding: 0 10px }
    }
  }
  // Reset nav.
  .top-area.group-nav-container {
    > .scrolling-tabs-container .nav a { padding: 10px 8px }
    > .nav-controls > div:last-child {
      padding-right: 8px;
      /// Groups -> Remove extra padding.
      &.append-right-10 { padding-right: 0 }
    }
  }
  [data-page ^= 'projects:forks:'] { .top-area { padding: 0 8px }}
  [data-page ^= 'projects:labels:'] { .top-area { padding: 0 i }}
  // Remove bottom border.
  .top-area, .wiki-page-header {
    &, .nav-links.mobile-separator { border-bottom: none }
  }
  .nav.nav-tabs {
    padding: 0 i;
    margin-top: 8px;
    rad: r;
    border-bottom: none;
    box-shadow: inner;
    c: 0 0 bg;
    .cover-block & {
      rad: 0;
      c: 0 0 t;
      box-shadow: none;
    }
  }
  .nav-links {
    li {
      &:first-child { margin-left: 8px }
      &:last-child { margin-right: 8px }
      a[class], a[href] {
        line-height: 32px;
        border-bottom-width: 1px i;
        c: cm;
        ../:hover > a { c: fg bd }
        ../.active, ../:active { > a { c: fg ac }}
        &.active { c: fg ac t }
      }
    }
    // Whitespace on the right side.
    + .nav-controls {
      padding: 8px;
      > form {
        margin-right: 7px;
        > input { height: 35px }
      }
      > *:last-child { margin-right: 0px }
    }
  }

  .scrolling-tabs-container {
    .fade-right {
      background: linear-gradient(to left, bg 45%, t);
      border-right: 1px solid bd;
    }
    .fade-left {
      background: linear-gradient(to right, bg 45%, t);
      border-left: 1px solid bd;
    }
  }


  // * Settings -> Billings.
  .billing-plans {
    [class *= 'card'] {
      c: 0 0 bg;
      .card-header {
        rad: r r 0 0;
        c: white 0 ac;
      }
      .card-body {
        rad: 0 0 r r;
        // Items.
        .feature-list li { c: 0 0 t }
        // Pricing.
        .price-per-month { c: ac }
        .price-per-year { c: green }
        // Borders.
        ../.current .card-body { c: 0 ac }
        ../:not(.current) .card-body { c: 0 bd }
      }
    }
  }

  // Dashboard -> Projects list.
  .projects-list {
    margin: 8px 0;
    rad: r;
    border: 1px solid bd;
    background-color: bg;
    // Items.
    > li {
      padding: 8px;
      transition: ts;
      border-bottom-color: bd;
      // On event.
      &:hover { c: 0 0 hl }
      // ? Round corners on first and last child.
      &:first-child { rad: r r 0 0 }
      &:last-child { rad: 0 0 r r }
      &.project-row { padding: 8px }
    }
    // Profile -> Compact list.
    &.compact {
      margin: 0;
      border: none;
      rad: 0 0 r r;
      .project-row { padding: 8px }
      .project-row:first-child { rad: 0 }
      // @upstream
      .s48.identicon { line-height: 40px }
    }
  }

  // Global -> Content lists.
  ul.content-list {
    /// Project -> Members.
    &.members-list:not(.qa-members-list) {
      margin-top: 10px;
      border: 1px solid bd;
      rad: r;
      > li { padding: 8px i }
    }
    > li {
      padding: 8px;
      c: fg bd;
      transition: ts;
      &:hover { c: 0 0 hl }
      a { color: ac }
    }
    li {
      color: fg;
      a[href] {
        color: ac;
        &.btn { color: fg }
      }
    }
    .group-row .stats { color: cm }
  }
  // Project -> Activity.
  .content_list {
    &:not(.list-unstyled) {
      margin-top: 10px;
      border: 1px solid bd;
      rad: r;
      c: 0 0 bg;
    }
    .event-item {
      padding: 8px 0 8px 56px;
      c: cm bd;
      transition: ts;
      &:hover {  c: 0 0 hl }
      .event-item-timestamp { margin-right: 8px }
      .event-user-info .author_name a { c: fg }
      .event-title {
        c: fg;
        .pushed, .commented, .opened { &, ~ span { c: cm }}
      }
      .system-note-image { left: 8px }
      .event-body a[href] { c: ac }
      // Remove left padding on small resolution.
      @media (max-width: 575px) { padding-left: 8px }
      // Corners.
      &:first-child { rad: r r 0 0 }
      &:last-child { rad: 0 0 r r }
    }
  }
  // Project -> Groups.
  #groups, #snippets, .snippets-list-holder {
    ul.content-list {
      margin-top: 10px;
      border: 1px solid bd;
      rad: r;
      c: 0 0 bg;
    }
  }

  // * Avatars.
  .avatar {
    c: cm;
    &, &-circle, &-container, &-tile {
      rad: var(--avatar) i;
      border: none i;
      c: fg t t;
    }
    &:not(img) {
      box-shadow: inset 0 0 0 2px bd;
      transition: ts i;
    }
    &.identicon {
      position: relative;
      &::before {
        content: '';
        position: absolute;
        top: 0; right: 0; bottom: 0; left: 0;
        opacity: 0.5;
      }
      &.bg1::before { c: 0 0 yellow }
      &.bg2::before { c: 0 0 magenta }
      &.bg3::before { c: 0 0 violet }
      &.bg4::before { c: 0 0 blue }
      &.bg5::before { c: 0 0 cyan }
      &.bg6::before { c: 0 0 red }
      &.bg7::before { c: 0 0 orange }
    }
  }
  // ? Drop some shadow; use transparent avatars for best results.
  img.avatar { filter: drop-shadow(1px 1px 2px sh) }

  // * Project details.
  .project-details {
    .text-plain { c: fg 0 0 0 }
  }
  .user-access-role { c: fg bd bg }

  // * Project -> New fork.
  .fork-thumbnail-container {
    .fork-thumbnail {
      rad: r;
      c: 0 bd bg;
      transition: ts;
      &:hover { c: 0 ac 0 }
      a > div { margin: 0 auto }
    }
  }

  // Project -> Commits page.
  #commits-list {
    .commit {
      padding: 8px;
      /// Headers.
      &-header {
        display: inline-flex;
        border: 1px solid bd;
        rad: r;
        c: 0 0 bg;
        .day { padding-right: 1ch }
        /// Whitespace between days.
        &:not(:first-child) { margin-top: 16px }
        /// Separate list from the header.
        + .commits-row { margin-top: 8px }
      }
      /// List.
      &s-row {
        position: relative;
        margin-bottom: 8px;
        border: 1px solid bd;
        rad: r;
        c: 0 0 bg;
        /// Connector.
        &::before {
          content: '';
          height: 8px;
          position: absolute;
          top: -9px; left: 28px;
          border-left: 1px solid bd;
        }
        ul > li:first-child { rad: r r 0 0 }
        ul > li:last-child { rad: 0 0 r r }
      }
    }
  }
  .commit {
    c: 0 bd;
    img { margin: 1px 0 0 1px }
    &-content {
      div.committer { c: cm }
      a { c: fg }
      /// Description.
      .commit-row-description {
        font-family: var(--ui-font);
        font-size: inherit;
        border-width: 4px;
        c: cm bd 0;
      }
    }
    /// Align SHA vertically.
    > .commit-detail { align-items: center }
  }

  // Commits -> Show commit message.
  .text-expander {
    c: 0 bd bg;
    transition: ts;
    &:hover { c: 0 0 bd }
    &.open {
      c: 0 ac ac;
      box-shadow: none;
      svg { v: white }
    }
    &:focus { outline: none }
  }

  // * Project :: Commit details page.
  // https://gitlab.com/vednoc/dark-gitlab/commit/9caacd40
  .commit-box {
    margin-bottom: 10px;
    padding: 8px;
    border: none;
    rad: r;
    c: 0 0 bg;
    box-shadow: inner;
    [class *= '-description'] {
      border-width: 4px;
      c: cm bd;
    }
  }

  // * Project :: Branches.
  .all-branches {
    > .branch-item {
      padding: 8px;
      c: 0 bd;
      .branch-title a { c: fg }
      .branch-commit {
        c: cm;
        .commit-sha { c: fg }
        time, .commit-row-message { c: cm }
      }
    }
    .btn.btn-remove { c: white red red }
    .branch-commit .commit-row-message { c: fg }
    // Select next sibling and reset the colors.
    & + .card-footer.text-center {
      c: fg 0 t;
      a { c: cm }
    }
  }
  // Graph color.
  .divergence-graph {
    .graph-side [class *= 'bar'] { c: 0 0 cm }
    .graph-separator { c: 0 0 cm }
    [class *= 'count'] { c: fg }
  }
  // ? Active/Stale/All pages.
  [data-page = 'projects:branches:index'] {
    .nav-links li { margin: 0 }
    .nav-controls { padding: 0 }
    .row-content-block + .all-branches,
    .top-area.adjust + .all-branches {
      margin-top: 10px i;
      rad: r;
      c: 0 0 bg;
      box-shadow: inner;
      li {
        &:hover { c: 0 0 bd }
        &:first-child { rad: r r 0 0 }
        &:last-child { rad: 0 0 r r }
      }
    }
  }

  // * Project -> Tag list.
  .tags > ul {
    margin-top: 10px;
    border: 1px solid bd;
    rad: r;
    c: 0 0 bg;
    li.flex-row {
      padding: 10px;
      transition: ts;
      .js-timeago, .commit-row-message { c: cm }
      .item-title.ref-name { c: fg }
    }
  }
  // * Project -> Tag details.
  .multi-line {
    .branch-commit {
      .commit-row-message { c: cm }
      .commit-sha { c: ac }
    }
    pre.wrap { c: 0 0 hl }
  }

  // * Project -> Contributors.
  [id *= 'contributors'] {
    .person .spark, .tint-box {
      rad: r;
      c: fg 0 bg;
      box-shadow: inner;
      .axis {
        .domain, line { v: 0 cm }
        text { v: fg }
      }
      // Graph colors.
      .area { v: green }
      .area-contributor { v: orange }
    }
  }
  /// New contributors charts. #97
  .contributors-charts {
    [fill = '#2e2e2e'] { fill: fg }
    [fill = '#919191'] { fill: cm }
    [fill = '#f2f2f2'] { fill: bd }
    [stroke = '#dfdfdf'] { stroke: bd }
    [style *= 'rgb(31\, 120\, 209)'] path { fill: rgb(31, 120, 209) i }
  }

  // Project -> Graph.
  .project-network {
    margin: 0 -16px;
    overflow: hidden;
    rad: r;
    c: 0 bd;
    .controls { c: 0 bd hl }
    // Graph background.
    .network-graph {
      c: 0 0 bg;
      // Hover over commit.
      > svg rect[rx *= '4'][ry *= '4'] {
        fill-opacity: 1 i;
        v: bd t;
      }
      [d ^= 'M165'], [d ^= 'M185'], [d ^= 'M195'] { v: bd 0 0 }
      // Text color.
      > svg text { v: fg 0 0 }
      // Sidebar.
      > svg rect[width *= '40'], > svg rect[width *= '30'] {
        stroke-width: 0;
        v: hl hl 0 0;
      }
    }
  }

  // * Global -> Modals.
  .modal, .modal-doorkeepr-auth {
    c: 0 0 t;
    &-backdrop.show, &-backdrop {
      opacity: 0.9;
      c: 0 0 bg;
    }
    .modal-content {
      border: none;
      rad: r;
      c: 0 0 bg;
      box-shadow: inner i;
      > :last-child { rad: 0 0 r r }
    }
    .modal-header {
      rad: r r 0 0;
      c: 0 bd t;
    }
    .modal-body {
      c: 0 0 t;
      // ? MR -> Merge with CLI instructions.
      pre {
        rad: r;
        c: cm bd hl;
      }
      [class *= '-subheader'] { c: 0 bd }
      // ? Shortcuts modal.
      .shortcut-mappings {
        th { c: fg }
        td { c: cm }
      }
      // ? Board scope.
      .title { c: cm }
      .block + .block { border-top-color: bd }
      // Actions in auth.
      .form-actions {
        rad: 0;
        c: 0 0 t;
        border: none i;
        box-shadow: 0 -1px bd;
      }
    }
    .modal-footer { c: 0 bd t }
  }

  // * Global -> Close buttons.
  .close {
    transition: ts;
    text-shadow: 0 1px 0 sh;
    &, &:hover, &:focus { c: cm }
    // Non-modal.
    &:not(:disabled):not(.disabled) .fa {
      text-shadow: 0 1px 0 sh;
      transform: translateY(-5px);
    }
  }

  // * Project -> Blame.
  [data-page *= 'projects:blame'] {
    .file-blame-legend { c: 0 bd bg }
    .blame table {
      rad: none;
      box-shadow: none;
      tr { c: 0 bd }
      tr:last-child > td::before { rad: 0 0 0 4px }
    }
    .blame-commit {
      position: relative;
      border-left: none i;
      &::before {
        content: '';
        position: absolute;
        top: 0;
        left: 0;
        bottom: 0;
        width: 4px;
      }
      /// Whitespace between avatar/content.
      .commit > a[href] > img { margin-right: 10px }
      /// Background.
      &:not(#z), & + .line-numbers { c: 0 0 hl }
    }
    /// Text.
    .cdark { c: fg }
    .commit-sha { c: ac }
    // ? Custom colors!
    for i in 0..9 {
      .legend-box-{i},
      .blame-commit-age-{i}::before {
        c: 0 0 green;
        opacity: s(('0.%s99'), i);
      }
    }
  }

  // Project -> Operations. #73.
  [data-page ^= 'projects:feature_flags:'] {
    /// Nav reset.
    .top-area {
      rad: r;
      border: 1px solid bd;
    }
    /// Environments.
    .gl-p-3.border-bottom.bg-gray-light {
      margin-top: 10px;
      rad: r;
      border: 1px solid bd;
    }
  }

  // Project -> Container Registry.
  .registry-placeholder {
    .container-image { c: 0 bd }
    .container-image-tags { margin-bottom: 16px }
    .table.tags td.action-buttons { opacity: 1 }
  }

  // * Project -> Various resets.
  .file-holder {
    .file-fork-suggestion { c: 0 bd bg }
    .file-title a:not(.btn) { c: fg }
  }

  // * Project -> Last commit.
  .info-well {
    rad: r;
    c: fg bd bg;
    > .well-segment {
      padding: 8px;
      c: cm bd;
      a {
        color: ac;
        img { margin: 1px 8px 0 0 }
      }
    }
    // Deleted branch.
    .limit-box {
      rad: r;
      c: white 0 orange;
      svg {
        margin-top: 3px;
        v: white;
      }
      .fa { c: white }
    }
    // Resets.
    .branch-link { margin-bottom: 0 }
    .branches .fork-svg { margin: 3px 2px -3px 0 }
    .vertical-align-middle { vertical-align: middle }
  }

  // Buttons -> GPG status.
  .gpg-status-box {
    &.valid {
      c: green green bg;
      &:hover { c: white 0 green }
    }
    &.invalid, &.invalid:focus {
      c: cm bd bg;
      &:hover { c: 0 0 hl }
    }
  }

  // * Popover.
  .popover {
    font-family: var(--ui-font);
    rad: r;
    c: 0 bd bg;
    box-shadow: bsh;
    &-header { c: 0 bd hl }
    &-body {
      padding: 8px;
      c: fg;
      ../.blue { c: 0 0 blue }
    }
    // Blue popouts?
    .arrow {
      color: bg;
      ../.blue .arrow { c: blue }
    }
    &[class *= '-top'] {
      .arrow::before { border-top-color: bd i }
      .arrow::after { border-top-color: inherit i }
    }
    &[class *= '-bottom'] {
      .arrow::before { border-bottom-color: bd i }
      .arrow::after { border-bottom-color: inherit i }
    }
    &[class *= '-left'] {
      .arrow::before { border-left-color: bd i }
      .arrow::after { border-left-color: inherit i }
    }
    &[class *= '-right'] {
      .arrow::before { border-right-color: bd i }
      .arrow::after { border-right-color: inherit i }
    }
    .gpg-popover {
      &-icon.valid svg { c: 0 green; v: green }
      &-icon.invalid svg { c: 0 red; v: red }
      &-user-link { c: fg }
    }
    // Milestones.
    .milestone-popover-body { c: 0 0 t }
    .milestone-popover-footer { c: 0 bd 0 }
  }

  // Popover -> Tour.
  .onboarding-helper-container {
    c: 0 0 bg;
    box-shadow: inner, 0 4px 8px 0 sh;
    .avatar {
      rad: 50% i;
      c: 0 0 bd;
      box-shadow: inner;
    }
    .progress { c: 0 0 bd }
    .bg-info { c: 0 0 blue }
    .text-info { c: blue }
    .qa-toggle-btn {
      c: 0 0 t;
      &:hover svg { v: fg }
    }
    .active { c: 0 0 bd }
  }

  // * Commits -> Diff summary.
  .diff-files-changed {
    rad: r;
    padding: 8px;
    margin: 10px 0;
    border: 1px solid;
    c: 0 bd bg;
    // Wrapper.
    > .files-changed-inner {
      // Hide buttons on small resolution.
      .inline-parallel-buttons {
        @media (max-width: 885px) { display: none i }
      }
      // ? Git stats.
      .commit-stat-summary { c: 0 0 t }
    }
  }
  // * Diff file.
  .diff {
    rad: r;
    // Collapsed file content.
    &-file .diff-content {
      rad: 0 0 r r;
      c: fg 0 bg;
      box-shadow: 0 -1px 0 0 bd;
      .btn-link { c: ac }
      // Gems/Packages/etc.
      // .code .line a { color: ac }
      // Inline diffs.
      .idiff { c: fg }
      .new .idiff, /.idiff.addition { c: 0 0 green }
      .old .idiff, /.idiff.deletion { c: 0 0 red }
    }
    // ? Fix borders on expanded sections.
    &-expanded { & > td, & + :not(&) > td { c: 0 bd 0 }}
  }

  // Diff.
  .diff-file {
    /// Images.
    .image { c: 0 0 t }
    .frame {
      c: 0 0 bd;
      // Added/removed images.
      &.deleted { c: 0 red }
      &.added { c: 0 green }
      // Broken image.
      img {
        background: none i;
        c: fg t 0;
      }
    }
    .view-modes {
      c: 0 0 t;
      ul li[class] {
        c: cm bd;
        text-decoration: none;
        transition: ts;
        &:hover { c: fg }
        &.active { c: ac }
      }
    }
    /// https://gitlab.com/veloren/veloren/merge_requests/478/diffs
    .file-container { c: 0 0 t }
    /// HACK: Resolves conflicts with 'global' badges.
    /:not(#z) span.label-lfs { c: fg bd bg }
    /// MR -> Resolve conflicts. #82
    &.conflict {
      border-top: none;
      .head, .origin {
        position: relative;
        &::before {
          content: '';
          opacity: 0.25;
          position: absolute;
          pointer-events: none;
          top: 0; right: 0; bottom: 0; left: 0;
        }
      }
      /// Modes.
      .line_content:not(.header) {
        &.head::before, &.origin::before { opacity: 0.35 }
        &.selected::before { opacity: 0.35 i }
      }
      .unselected {
        &:not(#z)::before { c: 0 0 bd }
        .btn { opacity: 0.5 }
      }
      .selected::before { opacity: 0.5 i }
      .head { &::before, > .btn { c: 0 green green }}
      .origin { &::before, > .btn { c: 0 blue blue }}
      /// Fix corners.
      tr.line_holder:last-child > td {
        &:first-child::before { rad: 0 0 0 r }
        &:last-child::before { rad: 0 0 r 0 }
      }
      .editor-wrap.saved .editor { c: 0 green }
    }
  }
  // * Diff -> Add note.
  .note-container {
    border: none;
    c: 0 bd t;
    box-shadow: 0 -1px bd;
    // Reply background.
    .new-note, .discussion-notes { c: 0 t }
  }
  /* Text color. */
  .branch-info > span { c: fg }

  // * Timeline content.
  .timeline-entry {
    c: fg bd t;
    &.outlined { c: 0 0 bg }
    // Entry borders.
    .timeline-entry-inner { c: 0 bd }
    // Highlighted entry.
    &:target, &.target { &:not(#z) { c: 0 0 hl }}
    // Fix button background.
    .note-action-button { c: 0 0 t }
    // Alerts -> Command alerts.
    .flash-container .flash-notice { c: white 0 blue }
  }

  // Notes -> Note.
  .note {
    // Notes on commits.
    &:not(.system-note) { c: 0 0 bg }
    // Profile links.
    .note-headline-light { c: cm }
    .note-headline-meta .system-note-separator {
      c: fg;
      text-decoration: none i;
      time { color: cm }
    }
    .system-note-message {
      p { c: cm }
      // Tasks -> https://gitlab.com/gitlab-org/gitlab-ce/issues/31358
      code {
        c: 0 0 hl;
        box-shadow: inner;
      }
    }
  }

  // Notes -> Discussion.
  .discussion {
    &-header { c: 0 bd } // Found within snippets.
    &-headline-light { c: cm }
    .timeline-entry {
      rad: 0;
      &:target, &.target {
        c: 0 0 hl;
        &:nth-child(3) { box-shadow: inset 0 -1px bd }
        &:nth-child(n+4) { box-shadow: inset 0 -1px bd, 0 -1px bd }
        &:nth-last-child(2) { box-shadow: 0 -1px bd }
        .code { c: 0 0 bg }
      }
      &:first-child { rad: r r 0 0 }
    }
    .discussion-wrapper {
      rad: r;
      // Show/hide replies.
      .replies-toggle {
        c: cm bd hl;
        &:not(.expanded) { border-bottom: 0 }
        &.collapsed { rad: 0 0 r r }
        span { c: cm }
      }
    }
  }
  // * Notes.
  .notes {
    c: 0 0 t;
    // Connectors.
    &::before {
      border-left-width: 1px i;
      c: 0 bd;
    }
    // Overwriten by user-link styles.
    a, a:hover { c: ac }
    // ? MR -> Notes -> Suggestions.
    .md-suggestion-header {
      rad: r r 0 0;
      c: fg bd bg;
      + table.code { rad: 0 0 r r }
    }
    .md-suggestion-diff { c: 0 bd }
    // Added/removed/changed things in discussions.
    .idiff {
      c: 0 0 hl;
      &.addition { c: green }
      &.deletion { c: red }
    }
  }

  // * Issues :: Conversation notes.
  [id ^= 'notes'] {
    // ? Note background, avoid system notes and forms.
    .notes.timeline > .timeline-entry:not(.system-note):not(.note-form) {
      border-radius: r;
      c: 0 0 bg;
    }
    // Icon.
    .system-note .timeline-icon {
      border: 1px solid bd;
      background-color: bg;
    }
    // ? Issue description.
    .detail-page-description > script + div {
      padding: 8px;
      border-radius: r;
      border: 1px solid bd;
      background-color: bg;
    }
  }

  // * Global -> Markdown input area.
  .md-area {
    padding: 0 i;
    rad: r i;
    c: 0 bd bg;
    &.is-focused {
      c: 0 ac;
      box-shadow: none i;
    }
    // Header.
    .md-header {
      &-tab {
        button[class] {
          border-width: 1px i;
          c: cm bd;
          &:hover { c: fg ac }
          ../.active button { c: fg ac }
        }
      }
      > ul.nav-links {
        rad: 0;
        margin: 0;
        padding: 8px 0 0 0 i;
        border-bottom: none;
        c: 0 bd t;
        box-shadow: inset 0 -1px bd;
      }
    }
    // Input area.
    .markdown-area {
      padding: 8px;
      font-family: var(--mono-font);
      c: fg bd t;
    }
    .md-preview-holder { padding: 8px }
    // When you use commands.
    .referenced-commands {
      border-top: 1px solid bd;
      rad: 0;
      c: 0 0 t;
    }
    .comment-toolbar {
      padding: 8px;
      c: cm bd 0;
    }
    /// Project -> Release edit page.
    /label[for = 'release-notes'] + .bordered-box {
      padding: 0 i;
      > .md-area { margin: 0 }
    }
  }
  // Edited text color.
  .edited-text { color: cm }

  // * Markdown area.
  .md {
    c: fg bg;
    a { c: ac }
    h1, h2 { c: 0 bd }
    // Hack for anchors reported in #61.
    for i in 1..6 {
      h{i} > a.anchor[href] {
        left: 0px;
        margin: 0 0 0 -10px;
        position: relative;
        font-size: 1rem;
        &::after { content: '#' }
      }
    }
    hr { c: 0 bd }
    kbd {
      border: 1px solid;
      c: cm bd bd;
      box-shadow 0 1px 0 sh, inset 0 0 0 2px bg i;
    }
    pre {
      &:not([class]) {
        rad: r;
        c: fg bd hl;
        code { c: 0 t t }
      }
      &.code {
        rad: r;
        c: 0 bd hl;
      }
    }
    caption { c: cm }
    mark { c: white 0 ac }
    p, a, :not(pre) {
      > code {
        c: fg bd hl;
        box-shadow: inner;
      }
    }
    blockquote:not(#z) {
      c: cm bd;
      border-left: 4px solid;
      &[dir] p, p { c: cm }
      > ul, > ol { margin-bottom: 0; c: cm }
    }
    img:not(.emoji) {
      border: none;
      c: ac bd bg;
      transition: ts;
      opacity: faded_img;
      &:hover { opacity: 1 }
    }
    ul, ol { ul, ol { margin-bottom: 0 i }}
    // Fullscreen mode.
    &-area .zen-backdrop.fullscreen {
      c: 0 0 bg;
      textarea { c: fg 0 t }
    }
  }

  // Global -> Left sidebar.
  .nav-sidebar {
    border-right: 1px solid bd;
    c: 0 0 bg;
    box-shadow: none;
    .divider { c: 0 bd }
    .context-header {
      > a, > button {
        c: fg;
        transition: ts;
        &:hover { c: 0 0 hl }
      }
    }
    /// Reset items.
    li {
      a { c: cm }
      /// NOTE: Causes flashes.
      // &, a { transition: ts }
      &:hover, &.active { .badge.badge-pill { c: fg }}
      .badge.badge-pill { min-width: 20px }
    }
    /// Nav.
    .sidebar-top-level-items > li {
      &:hover {
        c: 0 0 hl;
        a { c: fg }
      }
      /// Remove right margin.
      > a { margin-right: 0px }
      /// Active sub-level-items.
      &.active {
        c: 0 0 hl;
        box-shadow: inset 4px 0 ac i;
        a { c: fg }
        > ul li:hover {
          c: 0 0 bd;
          box-shadow: inset 4px 0 ac i;
        }
      }
      /// Don't apply styles to fly-out menu.
      &:not(.is-over) > ul li.active {
        c: 0 0 bd;
        box-shadow: inset 4px 0 ac i;
        > a { c: ac 0 t }
      }
      /// Fly-out menu.
      &.is-over > ul {
        c: 0 bd bg;
        box-shadow: bsh;
        a {
          c: cm;
          &:hover, &:focus { c: fg 0 hl }
        }
      }
      /// Item highlighting.
      &.active > a:hover, &.is-over > a { c: 0 0 bd }
    }
    /// Remove highlight. Replaced by the `li.active > ul li:hover`.
    .sidebar-sub-level-items > li a:hover { c: 0 0 t }
    /// Fix overflow/height on Firefox.
    .nav-sidebar-inner-scroll {
      height: calc(100% - 48px);
      overflow-x: hidden i;
      > ul.sidebar-top-level-items { margin-bottom: 0 }
    }
    /// Resets for collapsed sidebar.
    &.sidebar-collapsed-desktop {
      .sidebar-top-level-items > li.active {
        box-shadow: inset 4px 0 bd i;
      }
    }
    /// Collapse button.
    .close-nav-button, .toggle-sidebar-button {
      width: 100%;
      transition: ts i;
      c: 0 bd t;
      &:hover { c: 0 0 bd }
      .collapse-text {
        transition: ts;
        c: fg;
      }
    }
  }

  // * Right sidebar.
  .right-sidebar {
    c: 0 bd bg;
    // Separators.
    .block, .gutter-toggle { c: 0 bd }
    // ! Fix buttons for #52. @upstream
    .btn-link {
      line-height: 16px;
      text-transform: capitalize;
      &[class *= '-date-'] { margin-top: -2px }
      &[class *= '-remove'] { c: red }
    }
    abbr.value-type-dynamic {
      c: cm;
      text-decoration: underline;
    }
    // Progress in milestones.
    .progress { c: 0 0 bd }
    // Text color.
    a:not(.btn):hover { c: ac }
    .no-value, .sidebar-collapsed-icon { c: fg }
    .title {
      c: cm;
      // Edit buttons.
      > a, > button { c: cm }
    }
    // Adjust sidebar width.
    &.right-sidebar-collapsed {
      &, .block { width: 50px }
      .block:hover { c: 0 0 hl }
      .gutter-toggle { height: 55px }
    }
    .issuable-sidebar-header { height: 55px i }
    // Expanded state should take more available space.
    &.right-sidebar-expanded .issuable-sidebar {
      padding: 0 16px;
      .block {
        width: 100%;
        max-width: 264px;
      }
    }
    // Wiki -> Sidebar.
    &.wiki-sidebar {
      a { c: fg }
      .active a { c: ac }
    }
  }

  // * Project :: Wiki.
  [data-page *= 'wikis:show'] #content-body {
    // Wiki sidebar.
    .right-sidebar.wiki-sidebar {
      a {
        color: fg;
        &:hover { color: ac }
      }
      .active > a { color: ac }
    }
    // Text colors.
    .wiki-last-edit-by {
      c: cm;
      > strong { c: fg }
    }
  }

  // * Markdown -> Mermaid.
  .mermaid {
    // #21 and #65.
    /:not(.file-content) > .mermaid {
      padding-bottom: 10px;
      rad: r;
      c: 0 0 bg;
      box-shadow: inner;
    }
    // Boxes.
    rect.actor { v: hl cm }
    text.actor { v: fg }
    // Connectors.
    .actor-line { v: 0 fg }
    // Notes.
    .note { v: bd green }
    .noteText { &, tspan { v: green }}
    // Lines.
    .message {
      &Text { v: fg }
      &Line0, &Line1 { v: 0 cm }
    }
    .label {
      c: fg;
      text { v: fg }
    }
    .node {
      rect, circle, ellipse, polygon { v: hl cm }
    }
    .edgePath {
      .path { v: 0 cm }
    }
    .arrowheadPath, #arrowhead { v: fg }
    rect[style = 'fill:#e8e8e8;'] { v: bd cm }
    .section0 { v: blue }
    .sectionTitle0, .titleText { v: fg }
    .done0, .done1, .done2, .done3 { v: hl cm }
    .doneText0, .doneText1, .doneText2, .doneText3 { &:not(#z) { v: fg }}
    .active0, .active1, .active2, .active3 { v: cm blue }
    .task0, .task1, .task2, .task3 { v: cyan blue }
    .grid .tick {
      v: fg fg;
      line { v: 0 cm }
      text { v: fg }
    }
    .cluster rect { v: hl blue }
    .commit-id, .commit-msg, .branch-label { c: fg; v: fg }
    .loopLine { v: 0 cm }
    .loopText { v: cm }
    .labelBox { v: cm cm }
    .labelText { v: bd }
    .classLabel {
      .box { v: fg }
      text { v: bd }
    }
    .classGroup {
      rect { v: hl cm }
      line { v: 0 cm }
      text { v: fg }
    }
    .relation { v: 0 cm }
    #dependency, #composition, #extension { &Start, &End { v: fg fg }}
    #aggregationStart { v: cm fg }
    #aggregationEnd { v: fg fg }
  }

  // * Project :: Project/group home panel.
  [class $= '-home-panel'] {
    c: 0 t;
    // Text color.
    .text-secondary { c: cm }
    // Remove button backgrounds.
    .js-read-more-trigger { c: ac 0 t }
    // Metadata text color.
    .project-metadata * {
      c: cm;
      // Reset license button.
      &.project-license .btn { c: 0 0 t }
    }
    // Project tags.
    .home-panel-topic-list {
      c: 0 bd;
      // Fix line height.
      .text-nowrap { line-height: 22px i }
      svg.icon { margin-top: 4px i }
      .badge:hover { c: fg 0 bd }
    }
    // ? Repo stats.
    .project-stats {
      .add-license-link, .stat-link { c: cm }
      .project-stat-value { c: fg }
      .nav > .nav-item:first-child { margin-left: 0 }
    }
    // ? Repo buttons.
    .project-repo-buttons {
      .count-badge, .btn-xs { height: 34px }
      .count-badge {
        .star-count, .count { c: fg }
        &-count { c: 0 bd hl }
        &-button {
          font-size: 14px;
          c: fg bd bg;
          &:hover { c: 0 0 hl }
        }
      }
      .clone-dropdown-btn {
        font-size: 14px;
        c: white blue blue;
        svg { v: white 0 }
        &:focus { opacity: 0.6 }
      }
    }
  }
  // ? Default buttons.
  .project-buttons {
    // margin: 0;
    .nav {
      .btn { margin-top: 10px }
      // 'Auto DevOps enabled' and possibly other buttons.
      .stat-text {
        margin-top: 10px;
        c: cm bd bg;
      }
    }
  }

  // * Arrows.
  // ? Old GitLab design.
  .count-with-arrow {
    .count { c: 0 bd bg }
    .arrow::before { border-right-color: bd i }
    .arrow::after { border-right-color: bg i }
  }
  // * Remove unnecessary whitespace.
  .project-home-panel {
    // Separate title from repo buttons.
    .home-panel-title-row { margin-bottom: 8px }
    // Badges.
    .project-badges {
      margin: 8px 0;
      a[class] {
        margin: 0;
        display: inline-flex;
        &:not(:last-child) { margin-right: 6px }
      }
    }
    // Project description.
    .project-home-desc, .project-description > div > p { margin: 0 0 10px 0 i }
    // Buttons under badges.
    .project-repo-buttons {
      // Reset margins on all items.
      > div { margin: 0 }
      // Apply margins on the child elements.
      // ? Inline is needed for specificity.
      > div > div:not(:last-child), > div > .inline { margin: 0 8px 8px 0 i }
      // Right margin for buttons.
      .project-action-button { margin: 0 i }
    }
  }
  // * Separator.
  .project-stats {
    border-bottom: none;
    c: 0 bd;
    // ? Reset background-color on project page.
    .progress { c: 0 0 t }
  }
  // Language bar.
  .progress.repository-languages-bar {
    c: 0 0 t;
    .progress-bar {
      rad: 8px;
      // ? Fixes buggy bar in `.../vednoc/better-comments` repo.
      flex-grow: 1;
      // Apply right margin to all but the last child.
      &:not(:last-child) { margin: 0 4px 0 0 i }
      // Crystal language color.
      &[style $= '#776791'] { c: 0 0 black }
    }
  }

  // Project -> Settings.
  .settings {
    c: 0 bd;
    /// Deploy keys.
    &-message {
      rad: r;
      c: fg bd hl;
    }
    // ? Standalone sections i.e. under Advanced.
    .sub-section {
      rad: r;
      c: 0 bd bg;
    }
    .project-visibility-setting {
      rad: r r 0 0;
      c: fg bd bg;
    }
    .project-feature-settings {
      rad: 0 0 r r;
      c: 0 bd bg;
    }
    // Protected branches/tags/envs.
    &-content {
      [class ^= 'protected-'] {
        > .table-bordered { border: none }
        > .card-header {
          rad: r r 0 0;
          c: fg bd hl;
          box-shadow: inner;
          + .settings-message {
            margin-top: -2px;
            rad: 0 0 r r;
            box-shadow: inner;
          }
        }
      }
    }
  }
  // ? Service Desk -> Forward...
  #js-service-desk .card-header {
    rad: r;
    c: orange bd bd;
    h3 {
      margin: 0.75rem 0;
      font-size: 1.0rem;
    }
  }

  /* Commit button. */
  .commit-sha-group .label,
  .commit-sha-group .btn {
    color: fg;
    border-color: bd;
    background-color: bg;
  }
  /* Fix background in upload files. */
  .commit-message-container { background: none }
  /* Dropzone! */
  .blob-upload-dropzone-previews { border-color: bd }

  // Project -> Badges.
  .project-badge { transition: opacity ts }
  [src *= 'for-the-badge'], [data-canonical-src *= 'for-the-badge'] { rad: 4px }

   // Project -> Readme.
  .file-holder {
    rad: r;
    c: 0 bd bg;
    &.readme-holder {
      .file-title strong { font-weight: normal }
      /// Feature -> Center and limit readme content.
      if ( md_limit == 1 ) {
        .file-content:not(#z) {
          max-width: 950px i;
          if ( md_center == 1 ) { margin: 0 auto }
        }
      }
    }
    // ? Header.
    .file-title {
      // @upstream https://gitlab.com/gitlab-org/gitlab-ce/merge_requests/25648
      border-top: 1px solid;
      &, &-flex-parent {
        rad: r r 0 0;
        c: 0 bd hl;
        &.is-commit { min-height: 52px }
        // Buttons w/o `button-default` class.
        a { color: unset }
        .btn {
          font-size: inherit;
          padding: 2px 10px i;
          c: cm 0 0 0;
          /// Reset colors from Edit/WebIDE buttons.
          &-primary, &-inverted {
            c: cm bd bg;
            &:hover {
              opacity: 1;
              c: fg 0 hl;
            }
          }
        }
        // Diff -> Renamed file names. #43
        .idiff {
          &.addition { c: green 0 t }
          &.deletion { c: red 0 t }
        }
      }
    }
    // ? Body.
    .file-content {
      c: 0 0 t;
      &.blob-no-preview h4 { text-shadow: none }
      // @upstream
      &.code pre { rad: 0 }
    }
  }

  // * Wiki.
  // NOTE: Deprecated.
  .wiki:not(.use-csslab) {
    color: fg;
    a { c: ac }
    h1, h2 { c: cm bd }
    > p > code { border: 1px solid bd i }
    blockquote, .blockquote {
      padding: 8px 16px;
      border-left-width: 4px;
      c: fg bd;
      // Second rule solves blockquote weirdness in issues.
      p, * > p { c: cm }
    }
    pre { rad: r }
    pre, code { c: cm bd bg }
    img:not(.emoji) {
      border: none;
      rad: r;
      c: 0 bd bg;
    }
    kbd {
      c: ac bg sh;
      box-shadow: none;
    }
  }

  // * Project -> File table.
  .table-holder {
    border: none;
    // Project -> File table.
    .tree-table tr.tree-item {
      // Reset borders.
      > td {
        // border: none i;
        transition: ts;
        &:not(:first-child) { border-left: none i }
        &:not(:last-child) { border-right: none i }
      }
      &:last-child { border-bottom: none }
      &:hover, &.selected { > td { c: 0 0 hl }}
      // All text colors.
      * { c: fg }
    }
    // ? Fixes `pre` element on project/pages/domains page.
    tbody pre { c: cm bd bg }
  }

  // Text dividers.
  .middle-dot-divider::after { color: cm }
  /* Fix master/history/find file/web ide section when low-resolution. */
  @media (max-width: 740px) {
    .nav-block {
      flex-direction: column;
    }
  }

  // Profile -> Core styles.
  .user-profile {
    // User info cover.
    .cover-block {
      c: 0 0 bg;
      box-shadow: inset 0 -1px bd;
      [class *= 'fade-'] { border: none }
      .cover {
        &-title, &-desc { c: fg }
        &-status { padding: 8px 0 4px }
      }
    }
    // ? Calendar in activity. @obsolete
    #activity > .row-content-block { c: 0 bd bg }
    // ? Overview -> Adjust whitespace between panes.
    .row {
      @media (min-width: 980px) {
        .col-12 .append-bottom-default { margin-bottom: 0 }
        .col-lg-6:first-child { padding-right: 3px }
        .col-lg-6:last-child { padding-left: 3px }
      }
    }
    // ? Reset items and loader height.
    .overview-content-list {
      > .loading {
        &, > .loading {
          margin: 0 auto i;
          height: 50px;
        }
      }
      // @obsolete
      .projects-list { border: none }
    }
  }

  // * Profile -> Contribution calendar.
  .calendar {
    &-hint { c: cm }
    svg.contrib-calendar > g {
      text { v: fg }
      rect { v: ac }
      rect[fill = '#ededed'] { opacity: 0.05 }
      rect[fill ^= 'rgb(17'] { opacity: 0.20 }
      rect[fill ^= 'rgb(12'] { opacity: 0.45 }
      rect[fill ^= 'rgb(82'] { opacity: 0.70 }
      rect[fill ^= 'rgb(37'] { opacity: 1 }
    }
  }

  // Empty block.
  .nothing-here-block {
    padding: 8px;
    c: fg;
  }

  // * Profile -> Overview.
  .user-profile {
    // Sections.
    [class *= 'col-'] > [class *= '-block'] {
      margin-top: 16px;
      border: 1px solid bd;
      rad: r;
      c: 0 0 bg;
      // Reset blocks. @upstream
      > div {
        margin-top: 0;
        h4, h5 {
          font-weight: normal;
          font-size: 1rem i;
        }
      }
      // ! @obsolete
      > [class *= '-block'] {
        padding: 0.1px;
        h4, h5 { padding: 0 8px }
        + .prepend-top-10 { padding: 0 8px }
        // Hide 'activity' border.
        &:first-child { c: 0 t }
      }
      // Improve empty list.
      .border-bottom {
        padding: 0 8px;
        rad: r r 0 0;
        c: 0 t hl;
        + div {
          min-height: 32px;
          border-top: 1px solid bd;
        }
      }
    }
    // ? Content list items.
    .event-item {
      c: cm bd;
      transition: ts;
      padding: 8px 8px 8px 40px i;
      @media (max-width: 575px) { padding-left: 8px i }
      .system-note-image { left: 8px }
      .event-title { c: cm }
      .event-body *:not(.color-label):not(.gfm) { c: fg }
      .author_name a { c: fg }
      &:first-child { rad: r r 0 0 }
      &:last-child { rad: 0 0 r r }
      &:hover { c: 0 0 hl }
    }
    // Contributions for the selected day.
    .user-calendar {
      padding: 8px i;
      &-activities {
        .bordered-list li { padding: 8px }
        h4 { &, & + p { padding: 8px }}
      }
    }
  }

  // Project -> Cycle Analytics.
  #cycle-analytics {
    /// Introduction.
    .landing { c: 0 bd bg }
    .card-header { border-bottom: 1px solid bd i }
    /// Navigation.
    .stage-nav {
      margin-top: 4px;
      &-item {
        c: fg bd bg;
        &.active { box-shadow: inset 4px 0 ac i }
        &:hover:not(.active) {
          c: 0 0 hl;
          box-shadow: none i;
        }
        /// Text.
        .stage-empty, .not-available { c: cm }
      }
    }
    /// Related issues.
    .stage-event-item {
      c: cm bd;
      .total-time { &, span { c: cm }}
      .issue-link, .issue-date, .build-date { c: cm }
      .item-title a { c: fg }
    }
  }

  // * Project -> Insights.
  .insights-charts {
    .chart-canvas-wrapper {
      margin-top: 10px;
      rad: r;
      c: 0 0 bg;
      box-shadow: inner;
    }
  }

  // * Dashboard -> Sub-nav block.
  .row-content-block {
    margin-top: 10px i;
    padding: 0px;
    rad: r;
    border: 0px solid bd;
    c: fg 0 t;
    &.middle-block { margin-bottom: 10px }
    // Same as down below; probably outdated.
    &.white {
      border: 1px solid bd;
      background-color: bg;
    }
    // * Global -> *You pushed to...*
    > .event-last-push {
      padding: 8px;
      rad: r;
      c: 0 0 bg;
      // Smol button is no-no.
      .btn { padding: 8px }
      .event-last-push-text, .btn { font-size: 14px i }
    }
  }
  .content-block {
    margin-bottom: 10px;
    c: fg bd;
  }
  /* Sub-header found in compare branches. */
  .sub-header-block {
    border-color: bd;
    background-color: transparent;
    // Commits list.
    + .card {
      border-radius: r i;
      ul > li:last-child { rad: 0 0 r r }
    }
  }
  // Sticky file name.
  .diff-file .is-compare { top: calc(91px - var(--radius)) i }
  // * Project -> Compare.
  [class *= 'card'] {
    // Nothing to compare.
    &.bg-light {
      c: 0 0 bg;
      box-shadow: inner;
    }
    // Reset whitespace.
    > .content-list > li {
      padding: 8px 10px;
      transition: ts;
    }
  }
  /* 'x of x tasks completed' */
  .task-status { c: fg }
  /* Merged/closed. */
  .merge-request.merged,
  .issue.merged,
  .merge-request.closed,
  .issue.closed {
    border-color: var(--darker);
    background-color: var(--dark);
  }
  // ? New issues.
  .issue.today {
    c: 0 0 t;
    box-shadow: none;
    &:hover { c: 0 0 bd }
    &::before {
      content: '';
      border-left: 4px solid ac;
      position: absolute;
      top: 0; left: 0; bottom: 0;
    }
  }
  .issuable-list {
    // Issues -> Manual ordering (added in 11.2).
    &.manual-ordering:not(#z) {
      c: 0 0 t;
      > li {
        rad: r;
        c: 0 bd bg;
        box-shadow: bsh;
        transition: none;
        &:hover, &.sortable-chosen { c: 0 0 hl }
      }
    }
  }

  // Design (added in 11.2). #72.
  .design-detail {
    &::before {
      content: '';
      z-index: -1;
      opacity: 0.25;
      position: absolute;
      top: 0; right: 0; bottom: 0; left: 0;
      c: 0 0 bg;
    }
    // Discussions.
    .image-notes {
      c: 0 0 bg;
      .design-discussion {
        c: 0 0 bg;
        &::before { c: 0 bd }
        .reply-wrapper { c: 0 bd }
      }
    }
    // Badges.
    .frame, .image-notes {
      .badge-pill, .key {
        c: white bd ac;
        box-shadow: none;
      }
    }
    // TODO: Refactor with notes.
    .note, .discussion { &-headline-light { c: cm }}
    .note-headline-meta .system-note-separator { c: cm }
  }

  // * Dashboard -> Activity.
  .activities {
    c: 0 t;
    // @upstream
    > .is-smaller { width: 100% }
    // Fix RSS icon.
    .controls {
      top: 0;
      right: 8px;
      margin: 14px -2px 0 0;
      position: absolute;
      @media (max-width: 740px) { display: none i }
    }
  }

  // * Dashboard :: Operations.
  .operations-dashboard {
    // Remove extra padding.
    > nav, > div { padding: 0 i }
    // 'Search your projects' margin fix.
    .page-title-holder {
      margin-top: 16px;
      .append-bottom-default { margin-bottom: 0px i }
    }
    // Add projects.
    .tokenized-input-wrapper {
      rad: r;
      &.focus { box-shadow: 0 0 1px 1px ac i }
      .tokenized-input { c: fg 0 t }
      .input-token, .tokenized-input-token-remove { c: 0 0 bd }
    }
    // Cards.
    .card {
      rad: r;
      box-shadow: inner;
      .card-header { rad: r r 0 0 }
    }
    // * Skeleton.
    .dashboard-cards {
      .bg-light { c: 0 0 hl }
      .dashboard-card-skeleton-info::after {
        background-image: linear-gradient(to right, t 0%, bg 50%, t 100%)
      }
    }
    // Modals -> Add projects.
    /[id *= 'add-projects-modal'] .modal-body {
      padding: 16px;
      .project-list-item {
        padding: 0;
        margin: 0;
        rad: r;
      }
    }
  }

  // * Dashboard :: Issues/MR page.
  [class $= '-filters'] + .card {
    c: 0 t t;
    // List.
    .issuable-list {
      margin-top: 10px i;
      rad: r;
      border: 1px solid;
      c: 0 bd bg;
      // Round corners.
      li {
        &:first-child { border-radius: r r 0 0 }
        &:last-child { border-radius: 0 0 r r }
      }
      // Remove right padding on issues/merge requests page.
      .issuable-info-container { padding-right: 0 }
    }
  }

  // * Dashboard -> To-do list.
  .js-todos-all .card {
    margin-top: 10px;
    rad: r;
    c: 0 0 t;
    // List wrapper.
    .todos-list {
      rad: r;
      border: 1px solid bd;
      c: 0 0 bg;
      // List items.
      .todo {
        border: none;
        // Reset borders and round corners.
        &:not(:first-child) { border-top: 1px solid bd }
        &:first-child { border-radius: r r 0 0 }
        &:last-child { border-radius: 0 0 r r }
      }
    }
  }

  // * Issues -> Similar issues list.
  .issue-form .list-unstyled {
    padding: 8px;
    rad: r;
    c: 0 0 bg;
    box-shadow: inner;
    .suggestion {
      c: ac;
      &-state-open { v: green 0 }
      &-state-closed { v: red 0 }
    }
  }

  // * Issues :: Search or filter results.
  .filtered-search-box {
    rad: r;
    c: 0 bd bg;
    &.focus { &, &:hover { c: 0 blue; box-shadow: none }}
    // Fix corners.
    [class *= 'history-dropdown-toggle-button'] { rad: r 0 0 r }
    &-input-container .filtered-search { rad: 0 r r 0 }
    .btn-link { c: cm }
    // Search field.
    .form-control { c: fg t }
    // Search field words.
    .name { color: fg }
    // Search field button.
    .clear-search {
      rad: r;
      c: 0 0 bg;
    }
    // Search tokens (labels).
    .selectable {
      margin-right: 8px;
      rad: 2px;
      box-shadow: inner;
      .name { c: cm 0 hl }
      .value-container {
        margin-right: 0;
        c: fg 0 bd;
      }
    }
    .filtered-search-token > .name { c: fg 0 bd }
  }

  // Issues -> Details.
  .detail-page-header { c: fg bd }

  // * Epics -> Wrapper.
  .epic-page-container {
    // Remove extra padding.
    .issuable-details {
      padding: 0;
      margin-bottom: 10px;
      c: 0 bd;
      // Remove margin.
      .card-slim { margin-bottom: 0px }
    }
    // Reactions wrapper.
    + .content-block { c: 0 t }
  }
  // Time tracking.
  .time_tracker {
    .compare-label { c: cm }
    .compare-value { c: fg }
    .time-tracking-help-state { c: 0 bd bg }
  }
  // Epics -> Ancestors.
  .ancestor-tree {
    .vertical-timeline {
      &::before { c: 0 cm }
      &-icon { c: 0 0 bg }
    }
    .opened svg { v: green }
    .closed svg { v: blue }
  }
  // Edited.
  // ? Contains styles for milestone elements; find a better place.
  .detail-page-description {
    .title, &.milestone-detail { c: fg bd }
    .edited-text, .edited-text * { c: cm }
  }

  // * Issues -> Related issues.
  .related-issues-block {
    margin-top: 16px;
    .card-slim {
      border: 1px solid bd;
      .card-header { c: 0 0 hl }
    }
    // Issue metadata.
    .item-body {
      // Link to the issue.
      a.sortable-link { c: fg }
      // Milestone/weight/etc.
      /.item-weight span, .path-id-text { c: cm }
    }
    // Search Box
    input { c: fg 0 bg }
    // Tokens.
    .issue-token {
      rad: 2px;
      box-shadow: inner;
      &-reference { c: fg 0 bd }
      &-remove-button { c: 0 0 t }
    }
  }
  // Related merge requests.
  #merge-requests > .card-header { c: 0 0 hl }
  // Badges.
  .issue-count-badge, .mr-count-badge { c: 0 bd bg }
  .related-items-list {
    > li:last-child { rad: 0 0 r r }
    .path-id-text, .item-path-id { c: fg }
    .open { v: green }
    .closed { v: red }
  }
  /// Epics -> Related issues.
  .related-items {
    &-tree { border-top: 1px solid bd i }
  }
  // Epics -> Sortable issues.
  .sortable {
    &-container { c: 0 0 t }
    &-chosen .card { c: 0 0 hl }
    &-link { c: fg }
  }

  // Notes.
  .note-text pre { rad: r i }
  // ? Fixes missing bottom border for code.
  .notes .note-text > p:last-child { padding-bottom: 1px i }

  // * Global -> Cards.
  .card, .card-slim {
    rad: r;
    c: 0 bd bg;
    .card-header {
      rad: r r 0 0;
      c: fg bd hl;
      &:first-child:last-child { rad: r }
      .badge-pill { c: 0 0 bg }
    }
    .card-body {
      padding: 16px;
      c: 0 0 t;
      .text-plain { c: cm }
      // Reset actions on `user/project/pages` page.
      .form-actions { margin: 0 i }
    }
    .card-footer {
      padding: 8px;
      rad: 0 0 r r;
      c: 0 bd t;
    }
    .card-body, .card-footer {
      pre {
        rad: r;
        c: cm bd bg;
      }
    }
    // ? Project -> Releases.
    &.linked-card {
      .badge.badge-pill { c: fg }
      &::after {
        border: none;
        border-left: 1px solid bd;
      }
    }
    // Items on hover.
    > ul > li { &:hover, &.done-reversible { c: 0 0 hl }}
    // Project -> Pages.
    &.border-danger {
      c: 0 red;
      .card-header { c: 0 red hl }
    }
  }
  .card {
    /// Groups -> Members table.
    &-without-border {
      c: 0 0 t;
      > ul.content-list { c: 0 0 bg }
    }
  }

  // * Emoji menu.
  .emoji-menu {
    rad: r;
    c: 0 bd bg;
    box-shadow: 0 4px 8px 0 sh;
    input { c: 0 0 hl }
  }

  // * Auto-complete menu.
  .atwho-view {
    rad: r;
    c: cm bd bg;
    box-shadow: bsh;
    .atwho-view-ul {
      li {
        c: fg;
        // Matching text.
        strong { c: ac }
        // Light text.
        small, .params { c: cm }
        // Selected.
        &:hover, &.cur { c: fg 0 hl }
      }
    }
  }

  // * Boards -> Wrapper resets.
  .boards-list {
    padding: 10px 16px i;
    height: calc(100vh - 134px);
    .board {
      transition: ts;
      padding: 0 i;
      &:not(:first-child) { padding-left: 8px i }
      &-title-caret:hover { c: 0 0 bd }
      // Whitespace for the last card.
      .board-card:last-child { margin-bottom: 8px i }
      &-inner.rounded { rad: r i }
      // Welcome/promos.
      &-blank-state, &-promotion-state {
        rad: 0 0 r r;
        c: 0 0 bg;
      }
    }
  }

  // * Boards -> Base styles.
  .issue-boards-content {
    // Fullscreen.
    &.is-focused {
      c: 0 0 bg;
      // Fix list height.
      .boards-list { height: calc(100vh - 45px) }
    }
    // Reset search bar.
    .row-content-block {
      margin: 0 16px;
      // Fix search box.
      .filtered-search-box { rad: 3px }
    }
    // Board colors.
    .board-inner {
      c: 0 bd bg;
      // Board header.
      .board-title { c: fg bd }
      // Empty, default boards.
      [class $= '-state'] { c: fg 0 bg }
      // Showing all issues.
      .board { &-list-count, &-title-sub-text { c: cm }}
    }
    .issue-count-badge { c: 0 bd }
    // Weight cards.
    .board-card-weight { c: cm }
    // Fix sidebar gutter close svg. #46 @upstream
    .gutter-toggle {
      padding-right: 10px;
      svg { width: 11px }
    }
  }

  // Boards -> Cards.
  /// They turn into a tooltip on drag event.
  .board-card {
    c: 0 bd hl;
    box-shadow: 0px 1px 2px sh;
    transition: border-color ts;
    &:hover {
      c: 0 ac;
      box-shadow: 0px 2px 4px 0 sh;
    }
    // Text color.
    &-title a { color: fg }
    &-number { color: cm }
    // Assignee background.
    &-assignee:hover a { c: 0 0 t }
  }

  // Boards -> Resets.
  .board {
    // Fix issues block.
    &.is-collapsed .issue-count-badge { c: 0 0 t }
    // Drag'n'drop boards.
    > .board-inner {
      c: 0 bd bg;
      .board-title { border-bottom-color: bd }
    }
  }

  // * Boards -> Add issues.
  .add-issues-container {
    rad: r i;
    c: 0 0 bg;
    box-shadow: bsh;
    .add-issues-list .board-card {
      box-shadow: bsh;
      &.is-active { c: 0 ac }
    }
    .issue-card-selected {
      v: white;
      c: 0 ac ac;
    }
    // Reset styles.
    .add-issues-header {
      padding: 16px;
      rad: r r 0 0;
      c: 0 0 hl;
    }
    .nav-links {
      rad: r;
      box-shadow: inner;
    }
    .add-issues-footer {
      padding: 16px;
      rad: 0 0 r r;
      c: 0 0 hl;
    }
  }

  // * Issues :: Labels.
  .labels-container {
    padding: 0px;
    c: 0 0 t;
    // List.
    .manage-labels-list > li:not(.empty-message) {
      &:not(.no-border) {
        border: 1px solid bd i;
        box-shadow: none i;
      }
      &:not(.is-not-draggable) {
        margin-bottom: 8px;
        rad: r;
        c: 0 bd bg;
      }
    }
    .label {
      // Badges.
      &-description a { c: ac }
      &-badge-gray { c: fg bd bd }
      &-badge-blue { c: white blue blue }
      &-subscribe-button[data-toggle] {
        width: auto;
        padding: 3px 8px ;
        margin-top: -3px;
        font-weight: normal;
      }
    }
  }

  // * Issues -> Milestones.
  .milestones {
    padding: 0;
    rad: 0;
    c: 0 0 t;
    // Items.
    .milestone {
      padding: 16px;
      rad: r;
      c: fg 0 bg;
      box-shadow: inner;
      &:hover { c: 0 0 bg }
      &-range { c: cm }
      a[href] { c: ac }
    }
  }
  .milestone {
    padding: 8px;
    .label-badge {
      c: cm bd bg;
      &-gray { c: fg 0 bd }
      &-blue { c: white 0 cyan }
    }
    // Color in relatable issues.
    &-title { c: cm }
    &-banner-link { padding: 2px i }
    // Reset for boards.
    &-content {
      .card-header {
        c: 0 t t;
        + .content-list { box-shadow: inset 0 1px bd }
      }
      li:last-child { rad: 0 0 r r }
    }
  }

  // Details.
  .issuable-row {
    c: 0 0 t;
    .issuable-number { c: cm }
  }

  // Forms.
  .form-actions {
    padding: 8px;
    border: none;
    rad: r;
    c: 0 0 bg;
    box-shadow: inner;
  }

  // * Promotional.
  .user-callout {
    // Reset extra margins on the side.
    margin: 20px 0 10px i;
    &.promotion-callout {
      .bordered-box {
        padding: 8px i;
        rad: r;
        c: 0 bd bg;
      }
    }
    // ? Found in: .../repo/pipeline_schedules
    .bordered-box.content-block {
      padding: 8px i;
      rad: r;
      c: 0 bd bg;
    }
  }
  .banner-non-empty-state {
    padding: 5px 0 0 0;
    border: 1px solid;
    rad: r;
    c: 0 bd bg;
    &.append-bottom-20 { margin-bottom: 10px }
  }
  // ? Callout in .../project/environments
  .canary-deployment-callout {
    border-top: 1px solid bd;
    border-bottom: none;
  }
  // Milestones -> Burndown chart hint.
  .content-block.burndown-hint {
    rad: r;
    c: 0 bd bg;
    svg path[fill = '#fff'] { v: t }
  }
  // Milestones -> Burndown chart buttons.
  .js-burndown-data-selector .btn-inverted { c: blue blue t }
  // GitLab-Gold promotion.
  .container-fluid > #content-body > .container-fluid.pt-1 { padding: 0 }
  /// Global -> Banner component.
  .gl-banner {
    c: 0 bd bg;
    /.new-gl-button { box-shadow: inner }
  }

  // * Milestones -> Burndown chart.
  .burndown-chart {
    rad: r;
    c: 0 0 bg;
    box-shadow: inner;
    text { v: fg }
    .axis {
      &-label text { v: fg }
      &-label line, line, path { v: 0 cm }
    }
    // ? Legend!
    .legend {
      rect { v: 0 cm }
      text { v: fg }
    }
    .ideal.line { v: 0 cm }
    .actual.line { v: 0 green }
    [stroke = '#ddd'] { stroke: bd }
    [fill = '#1f78d1'] { fill: blue }
    [stroke = '#1f78d1'] { stroke: blue }
    // #83
    /.gl-legend { c: fg }
  }
  // Repository charts.
  .repo-charts {
    // Brings back the language color.
    .fa { color: inherit i }
    canvas:not(#languages-chart) { c: 0 0 bg }
  }
  // ? Found in: .../repo/pipelines/charts
  [data-page = 'projects:pipelines:charts'] {
    .legend {
      &-success .fa { color: inherit i }
      &-all { c: cm }
      &-all > i { color: inherit i }
    }
    canvas {
      rad: r;
      c: 0 0 bg;
      box-shadow: inner;
    }
  }

  // * Calendar.
  .pika-single {
    rad: r;
    c: fg bd bg;
    // Header.
    .pika-label { c: cm 0 t }
    .pika-prev, .pika-next { filter: invert(1) }
    // Cells.
    th { c: fg bd }
    td {
      c: 0 bd 0;
      .pika-day {
        c: fg 0 bg;
        &:hover, ../[class ^= 'is-'] .pika-day { c: ac 0 bd }
      }
      &.is-disabled .pika-day { c: cm 0 t }
    }
    // Shadow.
    &.is-bound { box-shadow: bsh }
    /// Global -> GitLab's wrapper around pika.
    /.gl-datepicker-theme {
      font-family: var(--ui-font) i;
      .pika-lendar:not(#z) {
        rad: r;
        box-shadow: inner;
      }
    }
  }

  // * Pagination.
  .pagination {
    // Rounded corners.
    > .page-item:first-child a {
      border-top-left-radius: r;
      border-bottom-left-radius: r;
    }
    > .page-item:last-child a {
      border-top-right-radius: r;
      border-bottom-right-radius: r;
    }
    // Links.
    .page-item {
      // Defaults.
      .page-link { c: cm bd bg }
      // Disabled.
      &.disabled .page-link {
        c: cm;
        filter: opacity(0.75) i;
      }
      // Selected.
      .page-link.active, &.active .page-link { c: fg 0 bd }
      &:hover:not(.disabled):not(.active) .page-link { c: fg 0 hl }
    }
  }

  // Global -> Issuable lists.
  .issuable {
    &-info-container a[href] { c: fg }
    &-updated-at { c: cm }
    &-comments a { c: fg }
    &-info {
      c: cm;
      a[href] { c: fg }
    }
    // Notes -> Confidential warning.
    &-note-warning {
      padding: 6px 8px 4px;
      rad: r r 0 0;
      c: orange bd bg;
      svg.icon { v: orange }
      a { &, &:hover { c: orange }}
      // Reset corners in confidential issues.
      + .md-area { rad: 0 0 r r i }
    }
    /// Email a new issue/mr.
    &-email-modal-btn { c: ac }
  }

  // * Issues/MR -> List.
  .issues-holder, .merge-requests-holder {
    margin-top: 10px;
    rad: r;
    border: 1px solid bd;
    c: 0 0 bg;
    li:not(.page-item) {
      transition: ts;
      &:first-child {
        rad: r r 0 0;
        &::before { rad: r 0 0 0 }
      }
      &:hover { c: 0 0 hl }
      // Approvals.
      /.text-success {
        c: green;
        svg { v: green }
      }
    }
    // ? Reset pagination.
    .gl-pagination {
      padding: 10px;
      margin-top: 0px i;
      border-top: 1px solid bd;
      .pagination { margin: 0px }
    }
    // Email a new issue/MR to this project.
    .issuable-footer {
      padding: 10px;
      border-top: 1px solid bd;
      rad: 0 0 r r;
      c: 0 0 hl;
    }
    /// Check all.
    /.check-all-holder { margin-left: 17px }
  }

  // * Merge Requests -> Details.
  .merge-request-tabs-holder, .epic-tabs-holder {
    margin: 0 0 10px;
    border: none;
    rad: r;
    c: 0 0 bg;
    box-shadow: inner;
    // Resets.
    .nav-links {
      margin: 0;
      rad: 0;
      c: 0 0 t;
      box-shadow: none;
    }
    > div > div:last-child { margin: 0 8px }
  }
  // First contribution.
  .fa-certificate {
    c: ac;
    + strong { c: fg }
  }

  // MR -> Resolved threads.
  .line-resolve-all {
    c: 0 bd bg;
    /.line-resolve-btn {
      &.is-disabled svg { v: cm }
      &.is-active svg { v: green }
    }
  }

  /* Merge Requests :: Notes. */
  .discussion-body .notes .note,
  .diff-file .notes .note {
    border-bottom-color: var(--darker);
  }
  /* Reply. */
  .discussion-form {
    background-color: transparent i;
  }
  // ? Discussion within diff section.
  .diffs {
    .notes {
      border-top: none i;
      &::before { border-left: 1px solid bd i }
    }
    .note.timeline-entry {
      c: 0 0 bg;
    }
  }
  // * Notes -> Reply section.
  .discussion-body, .diff-file {
    .discussion-reply-holder {
      c: 0 bd t;
      &:last-child { rad: 0 0 r r i }
      &.is-replying { c: 0 0 hl }
      + .new-note { c: 0 bd }
    }
  }
  // Comments disabled.
  .disabled-comment {
    margin-top: 10px;
    padding: 8px 0;
    rad: r;
    c: fg bd hl;
    a[href] { color: ac }
    [class *= 'warning'] { c: orange t t }
  }
  /* Text color. */
  .timeline .system-note .note-text,
  .notes .note-body .note-text {
    color: var(--light) i;
  }
  .notes .system-note .system-note-commit-list-toggler {
    color: var(--accent) i;
  }
  // 'Toggle commit list' background.
  .notes .system-note .note-body .note-text:not(.hide-shade)::after {
    background: none i;
  }

  // * Merge Requests -> Review.
  .draft-note-component { c: 0 0 t }
  .review-bar-component { c: 0 bd bg }

  // * Merge Requests :: Discussions in file diff.
  .diff-file .notes_holder {
    font-family: var(--ui-font);
    // ! Might not need this line anymore.
    .notes_line {
      c: fg bg bg;
      border-right-color: bd i;
    }
    // Note border/background colors.
    .notes-content { c: 0 bd t }
    // Different notes_content.
    .notes_content {
      c: fg bg bg;
      // Discussion.
      .notes {
        border-top: 1px solid;
        c: 0 bd bg;
      }
    }
  }

  // * Merge Requests :: Files.
  .diff-tree-list {
    .drag-handle:hover { c: 0 0 bd }
    .tree-list-holder {
      // Search.
      .tree-list-search input {
        rad: r;
        c: fg;
      }
      // Files.
      .tree-list-scroll {
        padding: 8px;
        overflow: auto;
        rad: r;
        border: 1px solid;
        c: 0 bd bg;
        .file-row {
          &:hover, &:active { c: 0 0 bd }
        }
      }
    }
  }

  // MR -> Widgets.
  .mr-state-widget {
    /// Merge/headings.
    .mr-source-target, .mr-widget-heading {
      rad: r;
      c: fg bd bg;
    }
    /// Collapse.
    .mr-widget-extension {
      c: fg bd hl;
      &:last-child { rad: 0 0 r r }
    }
    /// Global widgets.
    .mr-section-container {
      border: 1px solid bd i;
      rad: r;
      c: 0 0 bg;
      &::before { c: 0 bd }
      /// Ready to be merged.
      > .mr-widget-section {
        rad: 0;
        c: 0 bd;
      }
      /// Help.
      .mr-widget-help { c: cm }
      /// Empty MR notice.
      .mr-widget-empty-state { c: fg }
    }
    /// Tweaks for borders.
    .mr-widget {
      &-workflow > :first-child { border-top: none }
      &-workflow::before, &-border-top { c: 0 bd }
    }
    /// Reports. #64.
    .report-block {
      &-container {
        c: 0 bd hl;
        /.failed > svg { v: red }
        /.neutral > svg { v: cm }
        /.success > svg { v: green }
      }
      &-list-issue-parent { c: 0 bd hl }
    }
    /// Reset tables.
    .border-top {
      c: 0 bd;
      .table {
        margin: -1px i;
        width: calc(100% + 2px);
        > :not(:last-child) tr > * { rad: 0 i }
      }
    }
    /// Fix 'WIP status' for users that can't merge it. @upstream
    .mr-widget-body.media {
      .square + button { margin-right: 10px }
      .bold { margin-left: 0; c: cm }
    }
    /// Footers.
    .mr-widget-footer {
      c: 0 bd;
      .btn-link { c: ac }
    }
    /// Branch/pipeline name resets.
    .label-branch, .pipeline-number {
      color: ac;
      font-size: inherit;
      font-weight: normal;
      font-family: var(--ui-font);
      &.label-truncate > a { line-height: 1.1 }
    }
    /// Text colors.
    .bold, .report-block, .media-body, .code-text { c: fg }
    .diverged-commits-count, .coverage { c: cm }
    /// Icon resets. @legacy
    .circle-icon-container { c: fg bd }
    .git-merge-icon-container { c: fg bd bd }
  }

  // * MR -> Changes.
  .mr-version-controls {
    padding-bottom: 0;
    margin: 0;
    width: 100%;
    rad: r;
    border: 1px solid i;
    c: fg bd bg;
    // Reset the width when file tree is open. @upstream
    &.is-fileTreeOpen {
      margin: 0 i;
      width: 100% i;
    }
    > div { border-bottom: none }
  }

  // * File Diff :: Expanded view.
  .line_holder {
    // Show/hide comments.
    .diff-notes-collapse {
      c: 0 bd bg;
      &:hover { c: 0 0 bd }
    }
    // No new line.
    .new-nonewline { c: cm 0 t }
    // Unfold lines.
    td.unfold, td.line_content.match {
      font-style: italic;
      c: cm 0 t;
    }
    // Comment bubble.
    .add-diff-note {
      padding: 0 0 1px 1px;
      c: fg bd bg;
      &:hover { c: 0 0 bd }
    }
    // Highlight the hovered line.
    &:not(.parallel):hover, &.parallel td.is-over { c: 0 0 bd }
    // Highlight syntax line in parallel view.
    > td.is-over + .parallel { c: 0 0 bd }
    // Divider for right pane in side-by-side mode.
    &.parallel > td:nth-child(3) { box-shadow: -1px 0 bd }
  }
  /* Text colors. */
  .diff-stats-summary-toggler { c: blue }

  // * Snippets
  .project-snippets {
    // Fix file title height.
    .js-file-title  .file-header-content {
      line-height: 30px;
      .btn { padding: 8px 7px 2px 8px }
    }
  }
  // Edit button.
  .detail-page-header a,
  .snippet-edited-ago {
    color: var(--light);
  }
  // Toggle discussion.
  .note-action-button {
    color: fg;
    &:focus, &:hover { color: cm }
  }

  // Profile -> Snippets list.
  .snippets-list-holder {
    // Text colors.
    li.snippet-row {
      .title {
        a[href] { c: fg }
        .snippet-filename { c: cm }
      }
      .snippet-info { c: fg }
    }
  }
  // Snippets -> Reset block.
  [data-page *= 'snippets:show'] .row-content-block { padding: 0 }

  // * Project :: Members.
  .members-list {
    li.member { padding: 8px }
    .cgray time { c: cm }
    .member a.member-group-link { c: fg }
  }

  // File editor.
  .file-editor {
    .file-holder { rad: r }
    .toolbar-btn:hover svg { v: fg }
    /// Fix fonts.
    .btn, .encoding-selector, #preview { font-family: var(--ui-font) }
    /// Header.
    .nav-links {
      margin-bottom: 10px;
      border: none;
      c: 0 bd bg;
    }
    /// Branch name.
    .editor-ref { c: 0 bd t }
    /// GitLab templates.
    /.template-selector {
      &s-menu { c: 0 bd }
      &-dropdowns-wrap > div[class] { font-family: var(--ui-font) }
    }
  }

  // Ace editor.
  .ace {
    /// Wrapper. #75.
    &-tm {
      rad: 0 0 r r i;
      c: fg 0 bg;
    }
    /// Gutter.
    &_gutter {
      rad: 0 0 0 r;
      c: cm 0 bg;
      box-shadow: 1px 0 bd;
      ~/_gutter-cell { c: 0 0 t }
      + .ace_scroller { rad: 0 0 r 0 }
    }
    /// Highlighted line/selection.
    &_gutter-active-line, &_active-line { c: 0 0 hl }
    &_selected-word, &_selection {
      c: 0 0 blue;
      opacity: 0.3;
    }
    &_cursor { c: ac }
    /// Guides.
    &_indent-guide {
      background: none;
      box-shadow: inset -1px 0 bd;
    }
    &_print-margin { c: 0 0 bd }
    /// Search/replace popout.
    &_search {
      c: 0 bd hl;
      box-shadow: bsh;
      &_field { c: fg 0 bg }
      [action] { c: fg bd hl }
    }
    /// Options.
    &_search_options {
      margin: 4px 0 0 -2px;
      text-align: left;
      .ace_button {
        padding: 4px;
        rad: 3px;
        c: cm bd bg;
        &:hover, &.checked { c: fg 0 bd }
      }
    }
    /// Scroller shadow.
    &_scroller&_scroll-left { box-shadow: inset 17px 0 16px -16px sh }
    /// Syntax highlighting.
    &_url { c: ac }
    &_list { c: magenta }
    &_line, &_constant, &_numeric { c: fg }
    &_support { ~/_type&, ~/_function& { c: cm }}
    &_variable, &_string, &_comment { c: green }
    &_heading, &_storage, &_keyword, &_meta&_tag { c: blue }
  }

  // Footer links.
  footer.footer a {
    c: cm;
    &:hover, &:focus { c: ac }
  }

  // * Docs :: Search results.
  .algolia-autocomplete {
    [class *= 'dataset'] {
      c: fg bd bg;
      // Transparentize the content.
      [class *= 'docsearch-s'] { c: fg 0 t }
    }
    // Remove shadow.
    .ds-dropdown-menu {
      box-shadow: 0 4px 16px -4px sh;
      // Arrow.
      &:before { c: 0 bd bg }
    }
    // Header.
    [class *= 'header'] { c: cm bd }
    // Separator.
    .algolia-docsearch-suggestion--subcategory-column::before,
    .algolia-docsearch-suggestion--content::before { c: 0 0 bd }
    // Selected.
    [class $= '--content']:hover { c: 0 0 bd }
    // Matching result.
    [class *= 'highlight'] { box-shadow: inset 0 -2px ac i }
  }

  // * Main content.
  .main {
    background-color: transparent i;
    box-shadow: none i;
    // TOC.
    // Remove sidebar.
    *::-webkit-scrollbar { display: none i }
    // Alerts.
    blockquote {
      position: relative;
      c: 0 t bg;
      &::before {
        content: '';
        position: absolute;
        top: 0; left: 0; bottom: 0;
        rad: r 0 0 r;
        border-left: 4px solid blue;
      }
    }
    // Reset *introduced in...* blockquotes.
    blockquote {
      padding: 16px;
      rad: r;
      box-shadow: inner;
      > p { margin: 0 }
    }
    // Border colors.
    h2, h3, h4, h5, h6, .edit-on, .comments { c: 0 bd }
    // Text colors.
    a:hover, a:focus { text-decoration: underline }
    .article-metadata { c: fg }
    .highlight.plaintext code { c: fg }
    :not(pre) > code {
      rad: 3px;
      c: fg bd hl;
      box-shadow: inner;
    }
    img {
      transition: ts;
      opacity: faded_img;
      &:hover { opacity: 1 }
    }
  }

  // Feature -> Custom width for fixed layout.
  .layout-page .limit-container-width {
    /[data-page ^= 'projects:merge_requests:'] {
      .detail-page-header.pb-0.pt-0 { padding: 10px 0 i }
      .mr-version-menus-container { margin-bottom: 0 }
    }
    &.breadcrumbs,
    /.container-limited,
    &.container-limited,
    &.container-fluid .flash-container,
    .merge-request-tabs-container,
    .epic-tabs-container,
    .file-content {
      max-width: var(--width) i;
    }
    .emoji-list-container,
    .detail-page-header,
    .page-content-header,
    .commit-box,
    .info-well,
    .commit-ci-menu,
    .files-changed-inner,
    .limited-header-width,
    .limited-width-notes {
      max-width: calc(var(--width) - 32px) i;
    }
    .issuable-details {
      &:not(.merge-request-details) {
        max-width: calc(var(--width) - 32px) i;
      }
      .detail-page-description, .mr-source-target,
      .mr-state-widget, .merge-manually {
        max-width: calc(var(--width) - 32px) i;
      }
    }
  }

  /* Fix bugged left sidebar. */
  @media screen and (max-width: 1200px) {
    /* Cut transitions. */
    .page-with-contextual-sidebar,
    .nav-sidebar,
    .toggle-sidebar-button,
    .close-nav-button,
    .nav-sidebar a {
      transition-duration: 0s i;
    }
  }

  // IDE.
  .ide {
    /// Panels.
    &-view {
      c: fg;
      /// Panels.
      .multi-file-commit-panel {
        c: 0 bd hl;
        strong { c: fg }
        &:not([class *= 'right']) { padding-right: 0 }
        .ide-review-sub-header { c: cm }
      }
      /// Commit message.
      .multi-file-commit-form {
        c: 0 bd bg;
        > form, > .commit-form-compact { c: 0 bd }
        .ide-commit-message-field { c: 0 0 t }
        textarea {
          padding: 8px;
          c: fg;
          font-family: var(--mono-font);
        }
      }
      /// Edit area.
      .multi-file-edit-pane {
        c: 0 bd;
        /// Navigation.
        .multi-file-tabs {
          c: 0 0 hl;
          box-shadow: inset 0 -1px bd;
          li {
            c: cm bd hl;
            &.active {
              border-bottom-color: bg i;
              c: fg 0 bg;
            }
          }
        }
        /// Tabs/templates.
        ../../-mode-tabs { c: 0 bd bg }
        ../../-file-templates { c: fg bd bg }
        /// Commit view.
        ../../-commit-editor-header {
          c: fg bd bg;
          box-shadow: inset 0 -1px bd;
        }
      }
      /// Background after you push a commit.
      .multi-file-commit-panel-success-message { c: 0 0 bg }
      /// Diff minimap.
      .diffOverviewRuler { c: 0 0 hl }
      /// File finder maximum height.
      .dropdown-menu { max-height: 500px }
    }
    // .multi-file-commit-panel-success-message { c: 0 0 hl }
    /// Panel header.
    &-context-header {
      c: fg;
      .sidebar-context-title { c: fg }
    }
    /// Left sidebar.
    &-activity-bar {
      c: 0 0 hl;
      button {
        &::after { c: 0 0 bg }
        &:hover, &:focus { c: 0 0 bd }
        &.active {
          c: 0 bd bg;
          box-shadow: inset 3px 0 ac i;
        }
        /// Reset file icons.
        svg[class *= 'ic-file-'] { v: cm }
      }
    }
    /// Merge Requests / Branches.
    &-tree-header {
      /// Dropdown.
      .ide-nav-form {
        .tab-links { c: 0 bd }
        /// Tabs.
        .nav-links {
          li { margin: 0 }
          li a:not(.active) { c: 0 0 hl }
        }
        /// Search container.
        .filtered-search-wrapper {
          c: 0 bd bg;
          input { c: 0 0 t }
        }
        /// Project metadata.
        .ide-merge-request-project-path { color: cm }
      }
    }
    /// File tree.
    &-context-body .multi-file-commit {
      &-list-path { &:hover, &.is-active { c: 0 0 bd }}
      &-panel-inner-content, &-form { c: 0 bd bg }
      &-panel-inner-content {
        c: 0 bd bg;
        .ide-tree-header { c: fg bd }
        // Tree items.
        .file-row {
          c: cm;
          &:hover { c: 0 0 hl }
          &.is-active, &:active { c: 0 0 bd }
          .ide-entry-dropdown-toggle { c: fg 0 bg }
        }
      }
    }
    /// Right sidebar.
    &-right-sidebar {
      .multi-file-commit-panel {
        c: 0 bd bg;
      }
      .ide-activity-bar {
        c: 0 bd;
        button.active { box-shadow: inset -3px 0 ac i }
      }
    }
    /// Left -> Panel separators.
    &-commit-list-container { &, > header { c: 0 bd }}
    /// Left -> Fix button background.
    &-staged-action-btn { c: 0 0 t }
    /// Right -> Pipelines.
    &-right-sidebar {
      .ide-pipeline-header {
        padding: 8px;
        margin: 0;
        c: 0 bd;
      }
      .ide-pipeline-list {
        .nav-links { c: 0 bd }
        .nav-links li { margin: 0 }
      }
    }
    /// Callouts.
    .bs-callout { c: 0 0 hl }
    /// Resize.
    .drag-handle {
      &:hover { c: 0 0 bd }
      &.is-dragging { c: 0 0 ac }
    }
    /// Status bar.
    &-status-bar {
      c: 0 bd bg;
      button { c: 0 0 t }
    }
  }

  // IDE -> Monaco editor.
  .monaco-editor {
    c: fg 0 bg;
    &-background { c: fg 0 bg }
    /// Gutter.
    .margin {
      c: 0 bd t;
      .line-numbers { c: cm }
    }
    /// Editor features.
    .lines-content {
      .current-line  { c: 0 bd bd }
      .cursor { c: fg ac ac }
      [class *= 'cigr'] { box-shadow: inset 1px 0 cm }
    }
    .monaco-sash { c: 0 0 ac }
    /// Command palette.
    .monaco-quick-open-widget {
      rad: r;
      c: fg bd bg;
      box-shadow: outer, bsh i;
      .results-group { c: cm }
      .label-name {
        c: fg;
        .highlight { c: ac }
      }
      /// Focused.
      .monaco-tree-row {
        &:hover, &.focused { c: ac 0 bd }
      }
    }
    /// Input.
    .monaco-inputbox {
      c: fg bd bg;
      input { c: fg 0 hl }
    }
    /// Find palette.
    .find-widget {
      c: 0 0 bd;
      box-shadow: bsh;
      .button, .label { filter: invert(var(--bg-invert)) }
    }
    /// Accessibility.
    .accessibilityHelpWidget {
      c: fg bd hl;
      box-shadow: outer, bsh;
    }
    /// Context menu.
    .context-view .monaco-action-bar {
      c: 0 0 bg;
      box-shadow: inner, bsh i;
      .action-item {
        > a { c: cm 0 t }
        &.focused a { c: fg 0 hl }
        &.disabled a { c: 0 bd }
      }
    }
    .scroll-decoration { box-shadow: inset bsh i }
    .tokens-inspect-widget { c: fg bd bg }
    .findOptionsWidget {
      c: 0 bd bd;
      box-shadow: bsh;
    }
    .monaco-editor-hover { c: 0 bd bg }
    .cslr.selected-text { c: 0 0 bd 0 0 0 }
    .selectionHighlight { c: 0 0 bd }
    /// Suggestion.
    .tree {
      c: cm bd hl;
      box-shadow: bsh;
      .focused { c: fg 0 bd }
    }
    /// Syntax highlighting.
    .mtk1, .mtk3 { color: fg }
    .mtk10 { color: cm }
    .mtk4, .mtk20 { color: red }
    .mtk7, .mtk8 { color: green }
    .mtk5, .mtk6, .mtk23 { color: blue }
    .mtk14 { color: magenta }
    .mtk22, .mtk12 { color: cyan }
    .mtk11 { color: orange }
    /// Git status.
    .dirty-diff-added { c: 0 0 green }
    .dirty-diff-modified { c: 0 0 blue }
    .inline-deleted-margin-view-zone { c: 0 0 #ff000050 }
    .inline-added-margin-view-zone { c: 0 0 #00ff0050 }
    .char-delete, .line-delete { c: white sh #ff000050 }
    .char-insert, .line-insert { c: white sh #00ff0050 }
    .current-line-margin { c: 0 bd bd }
  }

  // Global -> Code sections.
  .code {
    c: fg 0 t;
    // Remove weird border around code wrapper.
    code { border: none }
    // Highlighted line.
    span.line.hll { c: 0 0 bd }
    // Gutter border.
    pre.code, .diff-line-num { c: 0 bd }
    // Gutters in file view/diff.
    .line-numbers, .diff-line-num {
      &, a { c: cm 0 t }
    }
    /// Code wrapper.
    .line_holder {
      // Lines for added/removed sections.
      .line_content {
        z-index: 1;
        c: fg 0 t;
        &.new::before, &.old::before { c: cm }
        &.old::after, &.new::after {
          content: '';
          z-index: -1;
          opacity: 0.25;
          top: 0; right: 0; bottom: 0; left: 0;
          position: absolute;
        }
        &.old::after { c: 0 0 red }
        &.new::after { c: 0 0 green }
      }
      // Gutters for added/removed sections.
      .diff-line-num.old, .diff-line-num.new {
        z-index: 1;
        border-right: none;
        padding-right: 11px;
        box-shadow: inset -1px 0 bd, -1px 0 bd;
        &.old::after, &.new::after {
          content: '';
          z-index: -1;
          opacity: 0.25;
          top: 0; right: 0; bottom: 0; left: 0;
          position: absolute;
        }
        &.old::after { c: 0 0 red }
        &.new::after { c: 0 0 green }
      }
      // Rounded corners.
      &:last-child > td {
        &:first-child, &:first-child::after { rad: 0 0 0 r }
        &:last-child, &:last-child::after { rad: 0 0 r 0 }
      }
    }
    // Diff expansion added in 11.2.
    .line_expansion {
      c: 0 0 t;
      td { c: 0 bd }
      a { c: ac }
    }
    // ? Notes -> Suggested changes.
    .gd, .gi {
      z-index: 0;
      position: relative;
      c: fg 0 t;
      // Fake element.
      &::before {
        content: '';
        position: absolute;
        top: 0; right: 0; bottom: 0; left: 0;
        opacity: 0.25;
        z-index: -1;
      }
    }
    // Removed/added.
    .gd::before { c: 0 0 red }
    .gi::before { c: 0 0 green }
    /// Syntax highlighting.
    &.white {
      .n, .gh, .gp { color: fg }
      .s, .c, .cp, .c1, .nn, .go { color: cm }
      .m, .nv, .na, .mf, .vi { color: cyan }
      .no, .nb, .nc, .kt { color: blue }
      .ss, .s1, .se, .gu, .nd { color: magenta }
      .s2, .sx, .sb, .sh, .nf, .gt, .sd { color: red }
      .nt, .si, .mi { color: violet }
      .err {
        c: red 0 t;
        box-shadow: inset 0 -1px red;
      }
    }
  }

  // CI/CD -> Pipelines.
  .table-holder {
    margin: 10px 0 0 0;
    .ci-table:not(.table) {
      border-collapse: separate;
      border: 1px solid bd;
      rad: r;
      c: 0 0 bg;
      tr {
        &:not([class]) > td { c: 0 0 bg }
        > th { c: fg darker hl }
        > td { padding: 8px }
      }
      .table-row-header {
        padding: 8px;
        c: fg bd hl;
      }
      > [class *= 'table-row']:nth-last-child(2) { border-bottom: none }
      .commit {
        padding: 8px;
        transition: ts;
        &:hover, &:hover { c: 0 0 hl }
      }
    }
  }

  // Pipelines.
  .pipeline {
    /// Fix thin font. @upstream
    &-graph .dropdown-counter-badge {
      top: 10px;
      font-weight: 300;
    }
    /// Pipelines -> Pipeline ID.
    &s .pipeline-id { c: fg }
  }

  // Pipelines -> Build.
  .build {
    // Build page.
    &-page {
      /// Header.
      .top-bar {
        margin-left: 0px;
        border-left: 1px solid bd i;
        rad: r r 0 0;
        c: cm bd hl;
        .btn { c: 0 0 t }
      }
      /// Build log.
      .build-trace {
        margin-left: 0px;
        border: 1px solid bd;
        border-top: none;
        rad: 0 0 r r;
        c: fg 0 bg;
        code { c: cm }
        .term-fg-red { c: red }
        .term-fg-l-green { c: green }
      }
      .environment-information { c: 0 bd bg }
      .build-loader-animation .dot { c: 0 0 bd }
    }
    // Build sidebar. #22.
    &-sidebar {
      .link-commit { c: ac }
      .builds-container {
        c: fg bd t;
        .build-job {
          transition: ts;
          &:hover { c: 0 0 hl }
          a { c: fg }
          // ? https://gitlab.com/gitlab-org/gitlab-ce/-/jobs/141718266
          &.retried { c: 0 0 t }
        }
      }
    }
    /.job-log {
      border: 1px solid bd;
      border-top: none;
      rad: 0 0 r r;
      c: fg 0 bg;
      .collapsible-line:hover { c: 0 0 hl }
      .log-duration-badge { c: 0 0 bd }
      .line-number { c: cm }
    }
  }

  // Pipelines -> Mini-pipelines.
  .mini-pipeline-graph-dropdown {
    &-toggle {
      c: 0 0 bg;
      &:focus { box-shadow: 0 0 4px 1px ac i }
    }
    &-menu {
      /// Indicator.
      &::after { c: 0 ac }
      /// Reset for items.
      li {
        &:hover a { c: cm 0 t }
        a:hover, a:focus { c: fg 0 bd }
        /// Pipelines where you have permissions.
        .ci-job-component {
          transition: ts;
          &:hover { c: 0 0 bd }
          .mini-pipeline-graph-dropdown-item {
            c: 0 0 t;
            .ci-status-text { c: cm }
            &:hover .ci-status-text { c: fg }
          }
          /// Retry button.
          .ci-action-icon-container {
            c: 0 bg bd;
            &:hover { c: 0 ac bd }
          }
        }
      }
      /// Fix text weight.
      .ci-build-text, .ci-status-text { font-weight: normal i }
    }
    /// Mini-pipeline connectors.
    /.stage-container::after { c: 0 gray }
  }

  // CI/CD -> Pipeline details.
  .pipeline-graph {
    margin-top: 8px;
    rad: r;
    c: fg 0 bg;
    box-shadow: inner;
    // Pipeline node.
    .build .build-content {
      c: cm bd bg;
      transition: ts;
      &:hover { c: fg ac 0 }
    }
    // Triangle.
    .cross-project-triangle {
      margin-top: 10px i;
      border-left-color: bd;
    }
    // Linked pipeline?
    // ? https://gitlab.com/gitlab-org/gitlab-ce/pipelines/31973329
    .linked-pipeline {
      &-content {
        rad: 10px i;
        c: fg bd bg;
        &:hover { c: 0 0 bd }
      }
      // Connector line.
      &::before { margin-top: 2px }
    }
    // Actions.
    .ci-action-icon-container {
      margin: 1px 1px 0 0;
      c: 0 bd bg;
    }
    // Connectors.
    .build, .stage-column .curve {
      &::before, &::after { c: 0 bd }
    }
    .ci-build-txt, .ci-status-text { font-weight: normal }
    /// v11.3 -> Full-width graph reset.
    /.tab-pane.position-absolute.w-100 {
      left: 1px i;
      .pipeline-graph { rad: 0 }
    }
  }

  // * Pipelines -> Mini pipelines in notes.
  // ? https://gitlab.com/gitlab-org/gitlab-ce/merge_requests/30080
  .linked-pipeline-mini-list {
    .arrow-icon { margin-top: 2px }
    .linked-pipeline-mini-item {
      margin-top: 5px i;
      > svg { margin: 1px 0 0 1px }
      // Fix size and on-hover event.
      &[class *= 'ci-status-icon-'] {
        width: 24px;
        height: @width;
        c: 0 0 bg;
        ../:hover > svg { margin: 0 }
      }
    }
  }

  // * Pipelines :: Failed Jobs.
  .build-failures {
    .table {
      margin-top: 10px;
      border: 1px solid;
      c: 0 bd bd;
      // Table header.
      > thead th { c: fg }
    }
    // Log output.
    .build-trace {
      c: 0 bg;
      > code { c: fg bd bd }
    }
    .build-name a { color: ac }
  }

  // * Pipelines :: Security.
  .split-report-section {
    margin-top: 10px;
    border: 1px solid;
    rad: r;
    c: fg bd bg;
    // Wrapper.
    .report-block-container { c: fg bd bg }
  }

  // * Jobs :: Show output.
  .build {
    // Breadcrumbs.
    &-header .header-content {
      margin: 4px 0;
      color: cm;
      a { color: fg;
        &:hover { color: ac }
      }
    }
    // Job details.
    &-light-text { color: cm }
    // Job on hover.
    &s-container a:hover { color: ac i }
  }

  // * Jobs :: Browse artifacts.
  [data-page = 'projects:artifacts:browse'] {
    .tree-table {
      rad: r;
      box-shadow: inner;
      thead {
        tr, tr > th { border-top: none }
        th:first-child { rad: r 0 0 0 }
        th:last-child { rad: 0 r 0 0 }
      }
      tbody {
        tr:last-child {
          td:first-child { rad: 0 0 0 r }
          td:last-child { rad: 0 0 r 0 }
        }
      }
      // Remove borders.
      tr {
        border: none i;
        td {
          border: none i;
          border-top: 1px solid bd i;
        }
      }
      // Item text color.
      .tree-item a { c: fg }
    }
  }

  // * CI/CD :: Schedules.
  [data-page ^= 'projects:pipeline_schedules'] {
    .ref-name, .next-run-cell { c: fg }
    .pipeline-schedule-table-row a { c: fg }
  }

  // Global -> Skeleton.
  .animation-container [class ^= 'skeleton-line-'] {
    background-color: bd;
    &::after {
      background-image: linear-gradient(to right, bd 0%, bg 100%)
    }
    /.mr-widget { &-heading, &-workflow::before { c: 0 bd }}
    /svg.gl-skeleton-loader rect { v: bd }
  }

  // * Groups.
  .group-nav-container {
    + .tab-content .has-no-search-results { c: cm }
  }
  // * Group header.
  .explore-groups {
    rad: r;
    c: 0 bd bg;
  }
  // * Group body.
  .groups-list-tree-container > ul.group-list-tree {
    margin-top: 10px i;
    border: 1px solid bd;
    rad: r;
    c: 0 0 bg;
    // On event.
    > .group-row {
      // Rounded corners.
      &:first-child > div { rad: r r 0 0 }
      &:last-child > div { rad: 0 0 r r }
    }
    .group-row {
      // Keep fake border even when sub-group is open.
      &.is-open > div { box-shadow: inset 0 -1px bd }
      // Horizontal line.
      &::before { c: 0 bd }
      // Vertical line.
      .group-list-tree::before {
        top: 0px;
        c: 0 bd;
      }
      // Remove top-border from the first item.
      &:first-child { border-top: none i }
      // Fix last item background.
      &:last-child::before { c: 0 0 t }
      // Content.
      &-contents {
        transition: ts;
        &:hover { c: 0 0 hl }
        // Text colors and width.
        .last-updated { c: fg }
        .stats { c: cm }
        &.has-more-items { padding: 10px }
      }
    }
  }

  // * Groups -> Contribution Analytics.
  .svg-graph-container {
    padding: 16px;
    margin-top: 16px;
    display: flex;
    justify-content: center;
    align-items: center;
    rad: r;
    c: 0 0 bg;
    box-shadow: inner;
    overflow-y: hidden; // Hide whitespace.
    svg {
      margin-top: -75px;
      text { v: cm 0 }
      path, line { v: 0 cm }
      rect {
        v: t;
        &.bar-rect { v: bd blue }
        &[fill = '#fff' i] { v: bg 0 }
      }
    }
  }

  // Global -> Invert image icons.
  // NOTE: Tweak if weird things happen.
  img[src *= '/assets/icon'] { filter: invert(1) }

  // * Invert illustrations.
  .wiki .anchor,
  .banner-graphic > svg,
  .explore-groups > .svg-container > svg,
  .commits-empty > svg,
  .artwork svg,
  .billing-plan-logo > svg,
  img[src *= '/assets/illustrations/'],
  #subgroups_and_projects .empty-state svg {
    filter: opacity(0.9) invert(0.9) hue-rotate(180deg) i;
  }

  // ! Trick for #44. @upstream
  img[src $= 'acc9ec5b5.svg'] {
    margin: -6px -16px 0 0;
    clip-path: circle(34px at 50% 48%);
    background: white;
  }

  // Feature -> Invert icons in Shadow DOM.
  if ( invert_ic == 1 ) {
    @supports not (-moz-user-select: none) {
      .created-icon, .opened-icon,
      svg.ic-upload, .ci-status-icon > svg,
      [class *= 'ci-status'] svg {
        filter: invert(0.8) hue-rotate(180deg);
      }
    }
  }

  // * Icons -> CI status icons.
  .ci-status {
    c: 0 0 bg;
    &[class *= 'ci-']:hover { c: 0 0 bd }
    // Replace icon's main fill color.
    ../ symbol[id ^= 'status_'] { path[fill = '#FFF' i] { v: bg }}
    // Colors!
    &.ci-success,
    &-icon-success, &-icon-passed {
      c: green green;
      svg { v: green }
    }
    &.ci-running,
    &-icon-running {
      c: blue blue;
      svg { v: blue }
    }
    &.ci-failed-with-warnings,
    &.ci-success-with-warnings, &-icon-failed-with-warnings,
    &-icon-pending, &-icon-warning, &-icon-success-with-warnings {
      c: orange orange;
      svg { v: orange }
    }
    &.ci-failed,
    &-icon-failed {
      c: red red;
      svg { v: red }
    }
    &.ci-created, &.ci-skipped, &.ci-canceled, &.ci-disabled, &.ci-manual {
      c: cm cm;
    }
    ^[0]-icon {
      &-skipped, &-created, &-canceled {
        c: 0 cm;
        svg { v: cm }
      }
      &-canceled, &-scheduled, &-disabled, &-not-found, &-manual {
        c: 0 cm;
        svg { v: cm }
      }
    }
    /// Warnings/alerts.
    /.danger_message { c: fg red hl }
    /.error-message { c: white 0 red }
    /.warning_message { c: fg yellow hl }
  }
  // * Icons -> Borderless CI status icons.
  .ic-status {
    &_warning_borderless { c: 0 orange }
    &_success_borderless { c: 0 green }
    &_running_borderless { c: 0 blue }
    &_failed_borderless { c: 0 red }
  }
  // Icons in epics.
  .issue-token-state-icon-open { v: green }
  .issue-token-state-icon-closed { v: blue }
  // Icons in activities.
  .commented-on-icon svg { v: blue }
  .created-icon svg { v: cyan }
  .opened-icon svg { v: green }
  .closed-icon svg { v: red }
  .accepted-icon svg { v: cyan }
  .deleted-icon svg { v: yellow }
  .imported-icon svg { v: violet }

  // * Centers project header elements; old design lookalike.
  if ( p_header == 1 ) {
    // Necessary in v11.6 (I think).
    .project-home-panel .project-header {
      > div { max-width: 100% }
    }
    .project-home-panel .project-badges {
      flex-direction: row i;
      justify-content: center;
    }
    // Up until v11.5 (I think).
    .project-home-panel > div {
      display: flex;
      flex-direction: column;
      margin-bottom: 0px;
      > div {
        justify-content: center;
        text-align: center;
      }
    }
    .project-stats .nav { justify-content: center }
    // Center the description.
    .project-description,
    .home-panel-title-row,
    .project-repo-buttons {
      margin: 0 auto;
    }
    // Separate buttons from the header and the description.
    .project-repo-buttons {
      margin-top: 10px;
      justify-content: center i;
    }
  }

  // Switch to our version of GitLab logo.
  if ( dark_logo == 1 ) {
    .tanuki-logo {
      filter: brightness(095%) drop-shadow(0px 1px 1px sh) i;
      .tanuki { &-right-eye, &-left-eye { v: ac t }}
      .tanuki-right { &-cheek, &-ear { v: bg t }}
      .tanuki-left { &-cheek, &-ear { v: bg t }}
      .tanuki-nose { v: bg t }
    }
  }

  // * Custom app background.
  // ? `:not(.dark)` is used to prevent disqus iframe conflicts.
  body:not(.dark)::before {
    position: fixed;
    z-index: -1;
    content: '';
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: var(--background);
    background-position: var(--bg-pos);
    background-repeat: var(--bg-repeat);
    background-size: var(--bg-size);
    filter: var(--bg-filter);
  }

  // * Code font.
  .diff-file table,
  code, .code, pre.code code,
  [class *= 'monospace'], .ref-name,
  .diff-line-num, .diff-line-num a,
  #editor, .monaco-editor * {
    font-family: var(--mono-font) i;
  }
  // Resets for code areas.
  .commit-sha, .ref-name, .git-revision-dropdown a {
    font-size: 100% i;
    font-family: var(--ui-font) i;
  }

  // Feat -> Fix alignment of icons from GitLab icons extension.
  if ( gl_icons == 1 ) {
    .tree-item-file-name > [class *= '-icon'] {
      margin: 0 i;
      width: 18px;
      display: inline-flex;
      justify-content: center;
    }
  }

  /* Prevent horizontal scrollbar from showing up when the viewport is overflowed. */
  html {
    overflow-x: hidden;
    background-color: var(--dark);
  }

  // Diffs.
  .diff-file .file-title {
    /// Fixes weird pseudoelements.
    &::before, &-flex-parent::before { display: none i }
    /// @upstream gitlab-org/gitlab/ffb01a13
    &, &-flex-parent { box-shadow: none }
  }
}

