    &-result[href] {
      border-color: bd i;
      &:hover { c: fg bd bd }
      .st-ui-type-heading {
        c: ac;
        em {
          c: white 0 ac;
          box-shadow: inset 0 -1px ac;
        }
      }
      .st-ui-type-detail, .st-ui-type-detail-bold {
