    &-info { c: blue }
    &-warning { c: orange orange hl }
    &-success { c: green 0 hl }
    &-danger { c: red 0 hl }
    &-gray { c: fg bd hl }
  }

  // * Left sidebar.
  .nav-wrapper {
    c: 0 bd bg;
    .global-nav {
      c: 0 0 t;
      &-cat.active a { c: ac }
      .collapse-toggle {
        c: 0 0 t;
        // transition: ts;
        &::after { c: 0 cm }
        &.active { c: 0 0 bd }
      }
      &-section.expanded {
        c: 0 0 t;
        box-shadow: inset 4px 0 0 ac;
      }
      a.active { c: cm ac bd }
      &-block-top a {
        color: fg;
        text-shadow: none;
      }
      &-link {
        color: cm;
        box-shadow: none;
        transition: 0s;
        &:hover, &:focus { &, + .collapse-toggle { c: fg 0 hl }}
        &.active[href] { c: ac }
      }
      .nav-link:hover { c: 0 0 hl }
    }
    // Collapse sidebar.
    .nav-toggle {
      c: 0 t t;
      box-shadow: 0 -1px bd i;
      &:hover { c: 0 0 hl }
      .label { c: fg }
      .arrow { &::before, &::after { c: 0 0 cm }}
    }
  }

  // * Right sidebar.
  .doc-nav {
    right: -12px;
    height: calc(100vh + 20px);
    c: cm 0 t;
    // Wrapper.
    #markdown-toc {
      a {
        text-decoration: none;
        c: cm;
        &:hover { c: fg }
        &.active { c: ac }
      }
    }
  }

  // * Search results page.
  // ? https://docs.gitlab.com/search/?q=test&idx=gitlab&p=1
  .instantsearch-input {
    .ais-search-box {
      c: 0 0 t;
      &--input {
        rad: r;
        c: fg bd bg;
        box-shadow: bsh;
        &:focus { c: 0 ac }
      }
      &--magnifier { pointer-events: none }
    }
    .search-results {
      .ais-stats {
        &, .stats {
          opacity: 1;
          c: cm;
        }
      }
      .ais-header {
        c: 0 bd;
        + .ais-body {
          label { c: fg }
          label > span { c: fg 0 bd }
        }
      }
      // Results.
      .hit {
        &-content {
          padding: 8px;
          font-size: 1rem;
          c: 0 t;
          box-shadow: none;
          em { c: ac 0 t }
        }
        &-text { c: cm }
        &-tag {
          rad: 0 0 3px 3px;
          c: fg t t;
        }
      }
      .ais-infinite-hits {
        rad: r;
        c: 0 0 bg;
        box-shadow: inner;
        &__empty { padding: 8px }
        &--item:not(:first-child) { box-shadow: 0 -1px bd }
        &--showmore > button {
          rad: r;
          border: 1px solid;
          c: cm bd bg;
        }
      }
    }
  }

  // * Code blocks.
  pre.highlight {
    rad: r;
    c: 0 bd hl;
    box-shadow: bsh;
    code {
      white-space: pre-wrap;
      word-break: break-all;
      rad: inherit;
      c: 0 t;
    }
    // Copy to clipboard.
    .clip-btn {
      top: 1px; right: 1px;
      rad: 0 r 0 0;
      c: cm 0 hl;
      .fa-clipboard::before { color: cm }
    }
    // Syntax highlighting.
    code, .na, .c, .c1 { c: cm }
    .n, .p, .nb { c: fg }
    .pi { c: red }
    .na { c: cm }
    .s1, .s2 { c: green }
    .no, .kp, .k { c: violet }
    .nt { c: blue }
    .se, .err { c: orange }
    .o, .s, .ss { c: cyan }
  }

  // Footer -> Sources.
  .source-link {
    c: cm 0 bg;
    box-shadow: 0 -1px bd;
    a[href] { c: ac }
  }
}

@-moz-document domain('status.gitlab.com') {
  // * Header.
  #statusio_header {
    margin: 0;
    display: flex;
    align-items: center;
    justify-content: space-between;
    // ? Make the header image smaller.
    #statuspage_header_image {
      float: left;
      width: 100%;
      max-width: 400px;
      &::before, &::after { clear: both }
    }
  }

  // * Status bar.
  [style *= 'd:#C44031'] { c: 0 0 red }
  [style *= 'd:#27AE60'] { c: 0 0 green }
  [style *= 'd:#00AAF0'] { c: 0 0 blue }
  [style *= 'd:#FFA837'] { c: 0 0 yellow }

  // * About section.
  .section_border_1 {
    c: 0 bd bg;
    a { c: ac }
    // Icons and text elements nested within them.
    .fa {
      span { c: fg }
      &-check { c: green }
    }
  }

  // * Table.
  .status_td { c: cm bd bg }

  // * History timeline.
  #statusio_history .history-filter-text, .back-text { c: cm }
  #statusio_history_timeline .timelineContainer {
    c: 0 bd;
    .incident_time { c: fg }
    .timelineMajor h2 {
      background-image: none i;
      &::after {
        content: '';
        position: absolute;
        margin-top: 20px;
        left: 16px;
        width: 24px;
        height: 2px;
        c: 0 0 bd;
      }
    }
    .timelineMinor dt {
      background: none;
      .open, .closed { c: cm }
      &::before {
        content: '';
        position: absolute;
        margin-top: 20px;
        left: 0px;
        width: 12px;
        height: 2px;
        c: 0 0 bd;
      }
    }
    hr { c: 0 0 bd }
  }

  // * Labels.
  .label-success { c: fg 0 green }
  .label-info { c: fg 0 blue }

  // * Rounded corners.
  .make_round {
    rad: r;
    &.make_round_bottom_only { rad: 0 0 r r }
    &.panel-heading { rad: r r 0 0 }
  }
  .component {
    &_first { rad: r r 0 0 }
    &_last { rad: 0 0 r r }
  }

  // * Footer.
  #bottom_menu {
    a { c: ac }
    strong a { text-decoration: underline }
  }
}

@-moz-document domain('next.gitlab.com') {
  body {
    background: none i;
    .wrapper > img.bg { opacity: 0 }
  }
  .white-box {
    rad: r;
    border: 1px solid;
    c: fg bd bg;
    a { c: ac }
    .text-switch { c: cm }
    .switch .cmn-toggle-round {
      + label { c: 0 0 bd }
      + label::after { c: 0 0 bd }
      + label::before { c: 0 0 bg }
      &:checked + label::before { c: 0 0 blue }
    }
  }
  .footer a { c: fg }
}

sections.code:
:root {
    --note: 'Dark-GitLab v0.56.1';
    --bshadow: 0 2px 4px var(--shadow);
    --t: transparent !important;
    --white: #fff;
    --width: 1280px;
    --radius: 4px;
    --avatar: 8px;
    --ts: 0.15s ease;
    --ui-font: 'font_name', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Noto Sans', Ubuntu, Cantarell, 'Helvetica Neue', var(--emoji-font), sans-serif;
    --mono-font: 'font_name', 'Menlo', 'DejaVu Sans Mono', 'Liberation Mono', 'Consolas', 'Ubuntu Mono', 'Courier New', 'andale mono', 'lucida console', monospace;
    --emoji-font: 'font_name', 'Apple Color Emoji', 'Segoe UI Emoji', 'Segoe UI Symbol', 'Noto Color Emoji';
    --bg-invert: 0;
    --bg-filter: opacity(1) invert(var(--bg-invert));
    --bg-size: cover;
    --bg-repeat: repeat;
    --bg-pos: center;
    --background: rgba(0,0,0,0.145);
    --dark: #292929;
    --darken: #333;
    --darker: #424242;
    --light: #e6e6e6;
    --lighter: #bababa;
    --accent: #cb253b;
    --shadow: rgba(0,0,0,0.145);
    --yellow: #f0c674;
    --orange: #d98245;
    --red: #a54242;
    --magenta: #b294bb;
    --violet: #85678f;
    --blue: #54738c;
    --cyan: #5e8d87;
    --green: #7b823a;
  }
  html {
    scrollbar-color: var(--darker) var(--dark);
  }
  ::-webkit-scrollbar {
    width: 12px;
  }
  ::-webkit-scrollbar-track {
    background: var(--dark);
  }
  ::-webkit-scrollbar-thumb {
    background: var(--darker);
  }
  ::placeholder {
    color: var(--lighter) !important;
  }
  ::-webkit-input-placeholder {
    color: var(--lighter) !important;
  }
  body {
    color: var(--light) !important;
    background-color: var(--t) !important;
    font-family: var(--ui-font);
  }
  body.modal-open {
    padding-right: 0px !important;
  }
  body.navless {
    color: var(--light) !important;
