/* ==UserStyle==
@name         GithubDarkTheme();
@namespace    github.com/DarkThemeHub/GithubDarkTheme
@version      1.24.0
@description  Dark theme for github.
@author       DarkThemeHub
@homepageURL  https://github.com/DarkThemeHub/GithubDarkTheme
@supportURL   https://github.com/DarkThemeHub/GithubDarkTheme/issues
@updateURL    https://raw.githubusercontent.com/DarkThemeHub/GithubDarkTheme/master/Generated/github.user.styl
==/UserStyle== */

@-moz-document regexp("^https?:\/\/(?!www\.google\.)(((?!desktop).*.)?(githubusercontent|github)\.(?!io|myshopify)).*") {
body {
    background-color: #222 !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-attachment: scroll !important;
    background-repeat: repeat !important;
    background-size: auto !important;
    background-position: left top !important;
}

.user-status-message-wrapper div {
    color: white;
}

.pinned-item-checkbox:checked + .pinned-item-name {
    background-color: #333;
}

.discussion-header,
.discussion-block-title,
.discussion-labels,
.discussion-block-header,
.branch-action,
.discussion-reply {
    background: #24292e;
}

.branch-action {
    background: transparent;
}

.graphs .selection .overlay {
    visibility: hidden !important;
}
.UnderlineNav-item {
    color: #bbb !important;
}

.UnderlineNav-item:focus,
.UnderlineNav-item:hover {
    color: white !important;
}

.d-flex {
    color: white !important;
}

.project-columns-container {
    background: #222;
}

a,
table.files td.icon,
table.files td.icon .octicon-file-directory,
.btn-link,
.commit-link {
    color: #4299fc;
}

.pl-smi,
.blob-code,
.blob-code-inner {
    color: #e8e8e8 !important;
}

.pl-en {
    color: #eaaa71 !important;
}

/* custom code font */

pre,
code,
tt,
kbd:not(.badmono),
pre,
samp,
.blob-code,
.file-data pre,
.line-data,
#gist-form .file .input textarea,
.blob-code-inner {
    font-family: "Menlo", Consolas, "Liberation Mono", Menlo, Courier, monospace !important;
}

/* Base color panel */

.btn:active,
option:hover,
.listings-layout .nav li a:hover,
.state-merged,
#fork-select .target:not(.disabled):hover,
.flash-notice,
table.upgrades tr:hover td,
.email-format .email-hidden-toggle:hover a,
.dashboard-tabs a:hover,
.dropdown-menu > li > a:hover,
.documentation-results a:hover,
.hidden-text-expander a:hover,
.dropdown-item:hover,
.dropdown-item.zeroclipboard-is-hover,
.subnav-item.selected,
.subnav-item.selected:hover,
.subnav-item.selected:focus,
.example-nav li a.selected,
.typeahead-result.navigation-focus,
.typeahead-result:hover,
.issues-reset-query:hover .octicon-x,
.segmented-nav li.active,
.diff-expander:hover,
.pagination .current,
.owner-select-target:hover,
.filter-item.selected,
.release-timeline-tags .expander-text:hover .expander-dots,
a.gollum-minibutton:hover,
a.gollum-minibutton:visited:hover,
.search-result-item.navigation-focus,
.gist-quicksearch-result.navigation-focus,
.gist-snippet .link-overlay .link,
.suggester li.navigation-focus,
.btn.btn-outline.zeroclipboard-is-hover,
.btn.btn-outline.zeroclipboard-is-active,
.select-menu-item:not(.select-menu-item-parent).navigation-focus,
.reaction-sort-item:hover,
.reaction-sort-item.selected,
.pull-request-link:hover,
.issues-reset-query:hover .issues-reset-query-block,
#com .dropdown-menu a:hover,
.date-selector td.selected,
.date-selector td.selectable-day:hover,
.add-line-comment,
button.add-line-comment,
.suggester .cur,
.autocomplete-item.selected,
.autocomplete-item.navigation-focus,
.select-menu-item.navigation-focus.selected,
.reblame-link:hover {
    background: #4183c4 !important;
}

.context-button:after {
    border-color: #4183c4 transparent transparent !important;
}

.octofication .message,
.panel-nav:not(.disabled):active,
.diagram-icon:not(.active),
.btn.btn-outline,
.btn.btn-outline:hover:not(.ajax-pagination-btn),
.btn.btn-outline.zeroclipboard-is-hover,
:checked + .radio-label,
.gist-snippet .file-box:hover .file,
.octofication .message,
.reaction-sort-item.selected,
.pull-request-link,
.timeline-progressive-disclosure-button:hover {
    border-color: #4183c4 !important;
}

.header-nav-link:hover .dropdown-caret,
.header-nav-link:focus .dropdown-caret,
#com #header .top-nav .dropdown-button:hover .dropdown-caret {
    border-top-color: #4183c4 !important;
}

.table-list .table-list-item.unread .table-list-cell-checkbox,
.Box-row--unread,
.Box-row.unread {
    border-left-color: #4183c4 !important;
    box-shadow: 2px 0 0 #4183c4 inset !important;
}

/* notification icon */

.notification-indicator .mail-status {
    background: #4183c4 !important;
    border-color: #1d1d1d !important;
}

/* text selection */

::selection {
    background-color: #4183c4 !important;
    color: #fff !important;
}

::-moz-selection {
    background-color: #4183c4 !important;
    color: #fff !important;
}

.CodeMirror-selected {
    background: rgba(255, 255, 255, 0.12) !important;
}

.CodeMirror::selection {
    background: rgba(255, 255, 255, 0.12) !important;
}

.CodeMirror::-moz-selection {
    background: rgba(255, 255, 255, 0.12) !important;
}

/* placeholder */

::-webkit-input-placeholder {
    color: #666 !important;
    opacity: 1 !important;
}

::-moz-placeholder {
    color: #666 !important;
    opacity: 1 !important;
}

::placeholder {
    color: #666 !important;
    opacity: 1 !important;
}

.select-menu-text-filter input::-webkit-input-placeholder {
    color: #555 !important;
}

.select-menu-text-filter input::-moz-placeholder {
    color: #555 !important;
}

.select-menu-text-filter input:-ms-input-placeholder {
    color: #555 !important;
}

.select-menu-text-filter input::placeholder {
    color: #555 !important;
}

/* focus hightlight */

a:focus,
button:focus,
[tabindex] {
    outline-color: #4183c4 !important;
}

input.focus[type="text"],
#adv_code_search .focus.search-page-label,

/* below selector has a ton of weight. Better to override it with
     class selectors that unset box-shadow and rules here. Testable
     on the repo tag editor */

input[type="text"]:focus:not(.js-color-editor-input):not(.tree-finder-input):not(.js-site-search-focus):not(.tag-input-inner),
.focused .drag-and-drop,
#adv_code_search .search-page-label:focus,
input.focus[type="password"],
input[type="password"]:focus,
input.focus[type="email"],
input[type="email"]:focus,
input.focus[type="number"],
input[type="number"]:focus,
input.focus[type="tel"],
input[type="tel"]:focus,
input.focus[type="url"],
input[type="url"]:focus,
textarea.focus,
textarea:focus,
#q:focus~button,
input.color-editor-input:focus,
#ghd-settings select:focus,
#ghd-settings input:focus,
.intgrs-lstng-item:hover,
.ajax-pagination-btn:focus,
.select-menu-item:focus,
.form-control.focus,
.form-select.focus,
.form-select:focus,
input.subnav-search-input:focus,
input[type="checkbox"]:focus,
.upload-enabled.focused {
    border-color: #4183c4 !important;
    box-shadow: inset 0 1px 2px rgba(0, 0, 0, 0.075), 0 0 2px #4183c4 !important;
    outline-color: #4183c4 !important;
}

/* inputs */

select,
input:not(.btn-link) {
    -moz-appearance: none !important;
    background-color: #25272a !important;
    border-color: #343434 !important;
    color: #eee !important;
}

input:not(.btn-link):focus {
    border-color: #2188ff !important;
}

/* custom checkbox and radio styles. We need a full set of styles here as
     WebKit removes all of them when -webkit-appearance: none is set. */

input[type="checkbox"],
input[type="checkbox"]:focus,
input[type="radio"],
input[type="radio"]:focus,
input[type="checkbox"]:hover:active,
input[type="radio"]:hover:active,
input[type="checkbox"]:disabled,
input[type="checkbox"]:hover:disabled,
input[type="checkbox"]:hover:active:disabled {
    -webkit-appearance: none !important;
    background-color: #25272a !important;
    border: 1px solid #484848 !important;
    color: #eee !important;
    height: 1em !important;
    width: 1em !important;
}

input[type="radio"] {
    border-radius: 100% !important;
}

input[type="checkbox"]:checked {
    background: url("data:image/svg+xml;base64,") !important;
    background-size: cover !important;
}

input[type="checkbox"]:indeterminate {
    background: url("data:image/svg+xml;base64,PHN2ZyB4bWxucz0iaHR0cDovL3d3dy53My5vcmcvMjAwMC9zdmciIHdpZHRoPSI1MTIiIGhlaWdodD0iNTEyIj48Zz48cmVjdCBoZWlnaHQ9IjUxMiIgd2lkdGg9IjUxMiIgeT0iMCIgeD0iMCIgZmlsbD0iIzAwMDAwMCIvPjxyZWN0IGhlaWdodD0iMTI4IiB3aWR0aD0iMzg0IiB5PSIxOTIiIHg9IjY0IiBmaWxsPSIjZGRkZGRkIi8+PC9nPjwvc3ZnPg==") !important;
    background-size: cover !important;
}

input[type="radio"]:checked {
    background: url("data:image/svg+xml;base64,PHN2ZyB4bWxucz0iaHR0cDovL3d3dy53My5vcmcvMjAwMC9zdmciIHdpZHRoPSI1MTIiIGhlaWdodD0iNTEyIj48Zz48cmVjdCBoZWlnaHQ9IjUxMiIgd2lkdGg9IjUxMiIgeT0iMCIgeD0iMCIgZmlsbD0iIzAwMDAwMCIvPjxjaXJjbGUgY3g9IjI1NiIgY3k9IjI1NiIgcj0iMTI4IiBmaWxsPSIjZGRkZGRkIi8+PC9nPjwvc3ZnPg==") !important;
    background-size: cover !important;
}

code {
    overflow: visible !important;
}

pre,
.highlight,
.diff-table,
.tab-size {
    tab-size: 4 !important;
    -moz-tab-size: 4 !important;
}

@supports (position: sticky) {
    .blog-aside {
        position: sticky !important;
        top: 8px !important;
    }
}

/* User time line firsts */

div[id^="contribution-joined-github"] img {
    padding-bottom: 20px !important;
}

/* PR toolbar tweaks */

.diffbar {
    padding-top: 10px !important;
    padding-bottom: 40px !important;
}

.is-stuck .diffbar {
    background: #25272a !important;
    height: 2.5em;
    pointer-events: all !important;
}

/* User profile: Make the border not cross into the years buttons */

.profile-timeline-month-heading:after {
