    color: hsl(0, 0%, 100%);
  }

  /* ... */

  ._4ea0e50d,
  .-Qjxs {
    background-color: inherit;
  }

  ._2SAWL {
    border-bottom-color: hsla(0, 0%, 100%, 0.1);
  }

  /* ... */

  ._2yODz {
