  }

  ._300Ea a {
    background: hsl(0, 0%, 6%);
    color: hsl(0, 0%, 96%);
    border-color: hsl(0, 0%, 14%);
  }

  ._300Ea a:hover {
    background: hsl(0, 0%, 12%);
    border-color: hsl(0, 0%, 16%);
  }

  ._2r0x5 {
    background-color: hsl(0, 0%, 20%);
    color: white;
  }

  ._13pLT {
    border-color: hsl(0, 0%, 28%);
  }

  /* ... */

  ._2YDRn {
    color: hsl(0, 0%, 60%);
  }

  ._3y8I5 label {
    color: hsl(0, 0%, 75%);
  }

  ._3y8I5 label:hover {
    color: hsl(0, 0%, 90%);
  }

  /* ... */

  ._2KmPM {
    box-shadow: 0 4px 13px -3px hsla(0, 0%, 0%, 0.2);
    border-bottom: 1px solid hsl(0, 0%, 12%);
  }

  .KyQaM,
  ._2uXWU {
    background: hsl(0, 0%, 16%);
  }

  ._3REog,
  ._2CEvd,
  .Nr1hY,
  header svg path {
    color: hsl(0, 0%, 100%);
    fill: hsl(0, 0%, 100%);
  }

  header ._1KOZP svg path {
    stroke: hsl(0, 0%, 100%);
  }

  ._8SWAs {
    border-color: hsl(0, 0%, 4%);
  }

  ._8SWAs::before {
    border-bottom-color: hsl(0, 0%, 4%);
  }

  ._8SWAs::after {
    border-bottom-color: hsl(0, 0%, 10%);
  }

  ._2U4fA {
    width: 215px;
  }

  ._3aGt_ {
    color: hsl(0, 0%, 100%);
  }

  ._2AVlX {
    background-color: hsl(358, 50%, 50%);
  }

  .marketing {
    background: hsl(0, 0%, 9%);
    color: hsl(0, 0%, 70%);
  }

  /* ... */

  ._1gHZ2._1c2N8 ._2tDwu,
  ._1gHZ2 svg,
  ._1gHZ2 .VComN,
  ._1gHZ2._3sUHq svg,
  ._1gHZ2._3sUHq .VComN,
  ._1gHZ2._3sUHq ._2tDwu,
  ._1gHZ2.rtKBH svg,
  ._1gHZ2.rtKBH .VComN,
  ._1gHZ2.rtKBH ._2tDwu,
  ._2tDwu {
    color: hsl(0, 0%, 100%);
    fill: hsl(0, 0%, 100%);
  }

  ._1gHZ2._1c2N8:hover {
    background-color: hsla(45, 100%, 61%, 0.2);
  }

  ._1gHZ2.rtKBH:hover {
    /* 	background: inherit; */
  }

  ._1gHZ2._3sUHq:hover {
    /* hsla(302, 57%, 50%, 0.2); */
  }

  /* ... */

  ._3QsIw {
