  }

  /* ._9dMXo svg path { */
  ._9dMXo ._3QsIw svg rect {
    fill: hsl(0, 0%, 100%);
  }

  ._1Bmfo svg,
  ._1Note svg path {
    fill: hsl(0, 0%, 65%);
  }

  ._3vqxt {
    /* 	background: hsl(0, 0%, 24%); */
    background-image: linear-gradient(-180deg,
        hsla(265, 40%, 24%) 0%,
        hsla(265, 40%, 36%) 100%);
    border-color: hsl(263, 24%, 56%);
  }

  ._3vqxt:hover {
