  }

  ._3yof6 table {
    /* border-bottom-color: hsla(0, 0%, 100%, 0.1); */
  }

  ._3yof6 td,
  ._3yof6 th {
    border-color: hsl(0, 0%, 100%, 0.1);
  }

  ._6d9832ac strong {
    color: hsl(0, 0%, 100%);
  }

  .markdown blockquote {
    border-left: 10px solid hsl(0, 0%, 24%);
    background: hsl(0, 0%, 4%);
  }

  .markdown td,
  .markdown th {
    background: hsl(0, 0%, 16%);
  }

  .markdown tr:nth-child(2n) td {
    background: hsl(0, 0%, 13%);
  }

  /* ... */

  .markdown,
  .markdown p,
  .markdown li {
    color: hsl(0, 0%, 90%);
  }

  .markdown .highlight {
    color: hsl(0, 0%, 90%);
  }

  .markdown .entity.name.function {
