    color: hsl(355, 65%, 65%);
  }

  .markdown .support.class {
    color: hsl(200, 90%, 65%);
  }

  .markdown .support.constant,
  .markdown .support.function,
  .markdown .support.type {
    color: hsl(187, 47%, 55%);
  }

  .markdown .comment {
    color: hsl(0, 0%, 70%);
  }

  .markdown .storage,
  .markdown .storage.type {
    color: hsl(286, 60%, 67%);
  }

  .markdown .keyword {
    color: hsl(187, 47%, 55%);
  }

  .markdown .constant.language {
