  }

  /* ... */

  .Ov0xh {
    color: hsl(0, 0%, 95%);
  }

  ._3RWfG {
    background: hsl(0, 0%, 24%);
    border-color: hsl(0, 0%, 36%);
  }

  /* ... */

  ._20QpA svg rect {
