  body {
    background-color: hsl(0, 0%, 12%);
    color: hsl(0, 0%, 100%);
  }

  a {
    color: hsl(358, 70%, 60%);
  }

  code {
    color: hsl(0, 0%, 75%);
  }

  pre code {
    background-color: hsl(0, 0%, 24%);
  }

  .header {
    background: hsl(0, 0%, 18%);
    border-top: 3px solid hsl(358, 60%, 50%);
  }

  footer p {
    color: hsl(0, 0%, 60%);
  }

  .faint {
    opacity: 0.8;
  }

  .note {
    background: hsl(0, 0%, 16%);
    border: 2px solid hsl(40, 100%, 70%);
    border-radius: 2px;
  }

  .category-articles .subtitle {
    color: hsl(0, 0%, 90%);
