  .cli-article .subtitle {
    color: hsl(0, 0%, 90%);
  }

  :root {
    scrollbar-color: rgba(175, 175, 175, 0.5) hsl(0, 0%, 10%) !important;
    scrollbar-width: thin !important;
  }
}

@-moz-document url-prefix("https://www.npmjs.com/login"),
regexp("^https://www.npmjs.com/package/.*/access$") {

  body,
  .vssy9 {
    background-color: hsl(0, 0%, 12%);
  }

  a {
    color: hsl(358, 80%, 60%);
  }

  .vssy9 header svg path {
    fill: hsl(358, 80%, 60%);
  }
}

